  "new_content": "/* Christmas Theme Styles */

/* CSS Variables for Christmas Theme */
:root {
  --christmas-red: #dc2626;
  --christmas-green: #059669;
  --christmas-gold: #fbbf24;
  --snowflake-color: #ffffff;
}

/* Christmas Background Pattern */
.christmas-bg {
  background-image: 
    radial-gradient(circle at 20% 50%, rgba(220, 38, 38, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 80%, rgba(5, 150, 105, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 40% 20%, rgba(251, 191, 36, 0.1) 0%, transparent 50%);
}

/* Falling Snowflakes Animation */
.snowflake {
  position: fixed;
  top: -10px;
  z-index: 1;
  user-select: none;
  pointer-events: none;
  animation: fall linear infinite;
}

.snowflake::before {
  content: "❆";
  color: var(--snowflake-color);
  font-size: 1.5rem;
  text-shadow: 0 0 10px rgba(255, 255, 255, 0.8);
}

@keyframes fall {
  0% {
    transform: translateY(-100vh) rotate(0deg);
    opacity: 1;
  }
  100% {
    transform: translateY(100vh) rotate(360deg);
    opacity: 0.3;
  }
}

/* Christmas Button Variants */
.christmas-button {
  background: linear-gradient(135deg, var(--christmas-red), var(--christmas-green));
  border: 2px solid var(--christmas-gold);
  box-shadow: 0 4px 15px rgba(220, 38, 38, 0.3);
}

.christmas-button:hover {
  background: linear-gradient(135deg, var(--christmas-green), var(--christmas-red));
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(5, 150, 105, 0.3);
}

/* Christmas Card Variants */
.christmas-card {
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.95), rgba(220, 38, 38, 0.05));
  border: 2px solid var(--christmas-gold);
  box-shadow: 0 8px 32px rgba(220, 38, 38, 0.1);
  backdrop-filter: blur(10px);
}

.christmas-card::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: 
    repeating-linear-gradient(
      45deg,
      transparent,
      transparent 10px,
      rgba(220, 38, 38, 0.05) 10px,
      rgba(220, 38, 38, 0.05) 20px
    );
  pointer-events: none;
}

/* Christmas Badge Variants */
.christmas-badge {
  background: linear-gradient(135deg, var(--christmas-red), var(--christmas-green));
  color: white;
  border: 1px solid var(--christmas-gold);
  box-shadow: 0 2px 8px rgba(220, 38, 38, 0.3);
}

.christmas-badge:hover {
  background: linear-gradient(135deg, var(--christmas-green), var(--christmas-red));
  transform: translateY(-1px);
  box-shadow: 0 3px 12px rgba(5, 150, 105, 0.3);
}

/* Christmas Header Styles */
.christmas-header {
  background: linear-gradient(135deg, var(--christmas-red), var(--christmas-green));
  color: white;
  position: relative;
  overflow: hidden;
}

.christmas-header::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: repeating-linear-gradient(
    45deg,
    transparent,
    transparent 20px,
    rgba(251, 191, 36, 0.3) 20px,
    rgba(251, 191, 36, 0.3) 40px
  );
  pointer-events: none;
  animation: shimmer 3s infinite;
}

@keyframes shimmer {
  0% {
    opacity: 0.5;
  }
  50% {
    opacity: 1;
  }
  100% {
    opacity: 0.5;
  }
}

/* Christmas Text Effects */
.christmas-text {
  background: linear-gradient(135deg, var(--christmas-red), var(--christmas-green), var(--christmas-gold));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 0 0 10px rgba(220, 38, 38, 0.5);
}

/* Christmas Border Glow */
.christmas-border {
  border: 2px solid var(--christmas-gold);
  box-shadow: 0 0 20px rgba(220, 38, 38, 0.3);
}

/* Christmas Shadow Effects */
.christmas-shadow {
  box-shadow: 0 10px 30px rgba(220, 38, 38, 0.2), 0 4px 15px rgba(5, 150, 105, 0.1);
}"
