    radial-gradient(circle at 10% 90%, rgba(147, 112, 219, 0.04) 0%, transparent 50%);
}

/* Enhanced Sun Rays Animation */
.sun {
  position: fixed;
  top: 10px;
  right: 10px;
  z-index: 1;
  user-select: none;
  pointer-events: none;
}

.sun::before {
  content: "\2600";
  color: var(--sun-color);
  font-size: 3.5rem;
  text-shadow: 0 0 25px rgba(255, 215, 0, 0.9);
  animation: pulse 2s infinite, float 3s ease-in-out infinite;
}

@keyframes pulse {
  0%, 100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.15);
    opacity: 0.85;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-8px);
  }
}

/* Enhanced Summer Button Variants */
.summer-button {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-pink));
  border: 2px solid var(--summer-blue);
  border-radius: 12px;
  color: white;
  font-weight: 600;
  box-shadow: 0 4px 15px rgba(255, 215, 0, 0.3), 0 0 20px rgba(255, 105, 180, 0.2);
  position: relative;
  overflow: hidden;
  cursor: pointer;
  transition: all 0.3s ease;
}

.summer-button::before {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
  transition: left 0.5s;
}

.summer-button:hover::before {
  left: 100%;
}

.summer-button:hover {
  background: linear-gradient(135deg, var(--summer-orange), var(--summer-yellow), var(--summer-pink));
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(135, 206, 235, 0.3), 0 0 25px rgba(255, 105, 180, 0.3);
}

.summer-button:active {
  transform: translateY(0) scale(0.98);
}

/* Enhanced Summer Card Variants */
.summer-card {
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.98), rgba(152, 251, 152, 0.03), rgba(255, 105, 180, 0.02));
  border: 2px solid var(--summer-blue);
  border-radius: 16px;
  box-shadow: 0 8px 32px rgba(135, 206, 235, 0.08), 0 0 30px rgba(255, 105, 180, 0.05);
  backdrop-filter: blur(10px);
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
}

.summer-card::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: 
    repeating-linear-gradient(
      45deg,
      transparent,
      transparent 10px,
      rgba(152, 251, 152, 0.03) 10px,
      rgba(152, 251, 152, 0.03) 20px
    ),
    radial-gradient(circle at 50% 50%, rgba(255, 105, 180, 0.05) 0%, transparent 50%);
  pointer-events: none;
}

.summer-card::after {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 215, 0, 0.2), transparent);
  transition: left 0.8s;
}

.summer-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 12px 40px rgba(135, 206, 235, 0.12), 0 0 35px rgba(255, 105, 180, 0.08);
}

.summer-card:hover::after {
  left: 100%;
}

/* Enhanced Summer Badge Variants */
.summer-badge {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-pink));
  color: white;
  border: 1px solid var(--summer-blue);
  border-radius: 20px;
  padding: 8px 16px;
  font-weight: 500;
  box-shadow: 0 2px 8px rgba(255, 215, 0, 0.3), 0 0 10px rgba(255, 105, 180, 0.2);
  position: relative;
  overflow: hidden;
  display: inline-block;
  transition: all 0.3s ease;
}

.summer-badge::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transform: translateX(-100%);
  transition: transform 0.3s;
}

.summer-badge:hover::before {
  transform: translateX(100%);
}

.summer-badge:hover {
  background: linear-gradient(135deg, var(--summer-orange), var(--summer-yellow), var(--summer-pink));
  transform: translateY(-2px);
  box-shadow: 0 4px 14px rgba(135, 206, 235, 0.3), 0 0 18px rgba(255, 105, 180, 0.3);
}

/* Enhanced Summer Header Styles */
.summer-header {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-blue), var(--summer-pink), var(--summer-purple));
  color: white;
  position: relative;
  overflow: hidden;
  padding: 2rem;
  border-radius: 0 0 24px 24px;
}

.summer-header::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: repeating-linear-gradient(
    45deg,
    transparent,
    transparent 20px,
    rgba(152, 251, 152, 0.2) 20px,
    rgba(152, 251, 152, 0.2) 40px
  );
  pointer-events: none;
  animation: shimmer 3s infinite, headerFloat 4s ease-in-out infinite;
}

@keyframes shimmer {
  0% {
    opacity: 0.4;
  }
  50% {
    opacity: 0.8;
  }
  100% {
    opacity: 0.4;
  }
}

@keyframes headerFloat {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-3px);
  }
}

/* Enhanced Summer Text Effects */
.summer-text {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-blue), var(--summer-pink), var(--summer-purple));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: none;
  filter: drop-shadow(0 0 10px rgba(255, 215, 0, 0.3));
}

/* Enhanced Summer Border Glow */
.summer-border {
  border: 2px solid var(--summer-blue);
  border-radius: 12px;
  box-shadow: 0 0 20px rgba(255, 215, 0, 0.25), 0 0 15px rgba(255, 105, 180, 0.15);
}

/* Enhanced Summer Shadow Effects */
.summer-shadow {
  box-shadow: 0 10px 30px rgba(255, 215, 0, 0.15), 0 4px 15px rgba(135, 206, 235, 0.08), 0 0 20px rgba(255, 105, 180, 0.08);
}

/* Enhanced Wave Animation */
.wave {
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 80px;
  background: linear-gradient(180deg, transparent, rgba(70, 130, 180, 0.3));
  z-index: 0;
  pointer-events: none;
  border-radius: 100% 100% 0 0;
}

.wave::before,
.wave::after {
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  background: rgba(70, 130, 180, 0.2);
  border-radius: 100% 100% 0 0;
}

.wave::before {
  bottom: 0;
  animation: wave 6s cubic-bezier(0.36, 0.45, 0.63, 0.53) infinite, waveFloat 5s ease-in-out infinite;
}

.wave::after {
  bottom: 0;
  animation: wave 7s cubic-bezier(0.36, 0.45, 0.63, 0.53) infinite, waveFloat 5s ease-in-out infinite;
  animation-delay: -2s;
}

@keyframes wave {
  0% {
