  position: relative;
  overflow: hidden;
  display: inline-block;
  transition: all 0.3s ease;
}

.summer-badge::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transform: translateX(-100%);
  transition: transform 0.3s;
}

.summer-badge:hover::before {
  transform: translateX(100%);
}

.summer-badge:hover {
  background: linear-gradient(135deg, var(--summer-orange), var(--summer-yellow), var(--summer-pink));
  transform: translateY(-2px);
  box-shadow: 0 4px 14px rgba(135, 206, 235, 0.3), 0 0 18px rgba(255, 105, 180, 0.3);
}

/* Enhanced Summer Header Styles */
.summer-header {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-blue), var(--summer-pink), var(--summer-purple));
  color: white;
  position: relative;
  overflow: hidden;
  padding: 2rem;
  border-radius: 0 0 24px 24px;
}

.summer-header::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: repeating-linear-gradient(
    45deg,
    transparent,
    transparent 20px,
    rgba(152, 251, 152, 0.2) 20px,
    rgba(152, 251, 152, 0.2) 40px
  );
  pointer-events: none;
