/* Enhanced Parallax effect for background */
.parallax {
  perspective: 1px;
  height: 100vh;
  overflow-x: hidden;
  overflow-y: auto;
}

.parallax__layer {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

.parallax__layer--base {
  transform: translateZ(0) scale(1);
}

.parallax__layer--back {
  transform: translateZ(-2px) scale(3);
}

.parallax__layer--deep {
  transform: translateZ(-4px) scale(5);
}

/* Enhanced Parallax stars */
.parallax__stars {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: transparent;
}

.parallax__star {
