  font-size: 4rem;
  margin: 0;
  font-family: 'Times New Roman', serif;
  text-shadow: 0 0 25px var(--quasar-yellow), 0 0 50px var(--quasar-yellow);
  animation: titleGlow 3s ease-in-out infinite alternate;
}

.subtitle {
  font-size: 1.4rem;
  margin: 15px 0;
  font-style: italic;
  color: var(--stellar-white);
  text-shadow: 0 0 15px var(--neutron-purple);
  animation: subtitleFade 4s ease-in-out infinite;
}

/* Enhanced Navigation styling */
.mobile-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(30, 58, 138, 0.98);
  backdrop-filter: blur(15px);
  padding: 15px 20px;
  z-index: 1000;
  background-image: var(--black-hole-pattern-3);
  background-size: 250px 250px;
  border-bottom: 2px solid var(--quasar-yellow);
  box-shadow: 0 5px 20px rgba(255, 107, 107, 0.3);
}

.nav-toggle {
  display: block;
  cursor: pointer;
  float: right;
  padding: 12px;
  background: none;
  border: none;
  outline: none;
  z-index: 1001;
  border-radius: 8px;
  transition: all 0.3s ease;
}

.nav-toggle:hover {
  background: rgba(255, 255, 255, 0.1);
}

.bar {
  display: block;
  width: 28px;
  height: 4px;
  margin: 6px auto;
  background: var(--stellar-white);
  transition: 0.4s;
  border-radius: 2px;
}

.nav-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.4s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.6s ease-in;
}

.nav-item {
  margin: 12px 0;
}

.nav-link {
  color: var(--stellar-white);
  text-decoration: none;
  font-weight: bold;
  padding: 14px 24px;
  display: inline-block;
  transition: all 0.4s ease;
  border-radius: 8px;
  font-size: 1.1rem;
}

.nav-link:hover {
  background-color: rgba(255, 255, 255, 0.15);
  transform: translateY(-2px);
  box-shadow: 0 5px 15px rgba(255, 255, 255, 0.2);
}

/* Enhanced Card styling */
.card {
  background: rgba(10, 10, 10, 0.97);
  padding: 40px;
  margin: 25px 0;
  border-radius: 15px;
  box-shadow: 0 0 35px rgba(255, 107, 107, 0.6);
  border: 3px solid var(--quasar-yellow);
  background-image: var(--black-hole-pattern-4);
  background-size: 180px 180px;
  transition: all 0.5s ease;
}

.card:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 50px rgba(255, 107, 107, 0.8);
  border-color: var(--accretion-orange);
}

.card h2 {
  color: var(--quasar-yellow);
  margin-top: 0;
  font-size: 2.5rem;
  text-align: center;
  text-shadow: 0 0 20px var(--quasar-yellow);
  margin-bottom: 25px;
}

/* Enhanced Timeline styling */
.timeline {
  position: relative;
  padding-left: 25px;
  margin: 30px 0;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 3px;
  background: linear-gradient(180deg, var(--quasar-yellow), var(--accretion-orange));
}

.timeline li {
  position: relative;
  margin-bottom: 25px;
}

.timeline li::before {
  content: '';
  position: absolute;
  left: -29px;
  top: 8px;
  width: 14px;
  height: 14px;
  background: var(--quasar-yellow);
  border: 3px solid var(--black-hole-dark);
  border-radius: 50%;
  box-shadow: 0 0 10px var(--quasar-yellow);
}

/* Enhanced Footer styling */
footer {
  text-align: center;
  padding: 40px 20px;
  background: var(--event-horizon);
  color: var(--stellar-white);
  margin-top: 50px;
  border-top: 2px solid var(--quasar-yellow);
  position: relative;
  overflow: hidden;
}

footer::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, var(--quasar-yellow), var(--accretion-orange));
}

footer a {
  color: var(--quasar-yellow);
  text-decoration: none;
  font-weight: bold;
  transition: all 0.3s ease;
}

footer a:hover {
  text-decoration: underline;
  color: var(--accretion-orange);
  transform: translateY(-1px);
}

/* Enhanced Black Hole-themed decorations */
.black-hole-decorations {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
}

.event-horizon {
  position: absolute;
  font-size: 2.5rem;
  animation: rotate 15s linear infinite;
  color: var(--wormhole-pink);
  text-shadow: 0 0 20px var(--wormhole-pink);
}

.event-horizon:nth-child(1) {
  top: 8%;
  left: 8%;
  animation-delay: 0s;
}

.event-horizon:nth-child(2) {
  top: 18%;
  right: 8%;
  animation-delay: 3s;
}

.event-horizon:nth-child(3) {
  bottom: 8%;
  left: 18%;
  animation-delay: 6s;
}

.accretion-disk {
  position: absolute;
  font-size: 2rem;
  animation: spin 8s linear infinite;
  color: var(--accretion-orange);
  text-shadow: 0 0 15px var(--accretion-orange);
}

.accretion-disk:nth-child(4) {
  top: 25%;
  left: 45%;
  animation-delay: 0s;
}

.accretion-disk:nth-child(5) {
  bottom: 25%;
  right: 45%;
  animation-delay: 4s;
}

.stellar-remnant {
  position: absolute;
  font-size: 2.5rem;
  animation: pulse 6s ease-in-out infinite;
  color: var(--neutron-purple);
  text-shadow: 0 0 25px var(--neutron-purple);
}

.stellar-remnant:nth-child(6) {
  top: 45%;
  left: 45%;
  animation-delay: 2s;
}

/* Enhanced animations */
@keyframes rotate {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes pulse {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.3); opacity: 0.8; }
}

@keyframes cosmicWave {
  0%, 100% { transform: translateY(0) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(2deg); }
}

@keyframes titleGlow {
  from { text-shadow: 0 0 20px var(--quasar-yellow), 0 0 40px var(--quasar-yellow); }
  to { text-shadow: 0 0 30px var(--quasar-yellow), 0 0 60px var(--quasar-yellow), 0 0 80px var(--quasar-yellow); }
}

@keyframes subtitleFade {
  0%, 100% { opacity: 0.8; }
  50% { opacity: 1; }
}

/* Enhanced Parallax effect for background */
.parallax {
  perspective: 1px;
  height: 100vh;
  overflow-x: hidden;
  overflow-y: auto;
}

.parallax__layer {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

.parallax__layer--base {
  transform: translateZ(0) scale(1);
}

.parallax__layer--back {
  transform: translateZ(-2px) scale(3);
}

.parallax__layer--deep {
  transform: translateZ(-4px) scale(5);
}

/* Enhanced Parallax stars */
.parallax__stars {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: transparent;
}

.parallax__star {
  position: absolute;
  background: var(--stellar-white);
  border-radius: 50%;
  animation: twinkle 3s infinite, drift 20s linear infinite;
}

.parallax__star:nth-child(1) {
  width: 1px;
  height: 1px;
  top: 12%;
  left: 22%;
  animation-delay: 0s;
}

.parallax__star:nth-child(2) {
  width: 2px;
  height: 2px;
  top: 52%;
  left: 82%;
  animation-delay: 0.7s;
}

.parallax__star:nth-child(3) {
  width: 1px;
  height: 1px;
  top: 92%;
  left: 42%;
  animation-delay: 1.4s;
}

@keyframes twinkle {
  0%, 100% { opacity: 0.4; }
  50% { opacity: 1; }
}

@keyframes drift {
  0% { transform: translateX(0); }
  100% { transform: translateX(10px); }
}

/* Enhanced Parallax galaxies */
.parallax__galaxy {
  position: absolute;
  background: radial-gradient(circle, var(--stellar-blue) 0%, transparent 70%);
  border-radius: 50%;
  animation: rotate 30s linear infinite, pulse 10s ease-in-out infinite;
}

.parallax__galaxy:nth-child(1) {
  width: 250px;
  height: 250px;
  top: 15%;
  left: 35%;
  animation-delay: 0s;
}

.parallax__galaxy:nth-child(2) {
  width: 180px;
  height: 180px;
  top: 75%;
  right: 25%;
  animation-delay: 15s;
}

@keyframes pulse {
  0%, 100% { transform: scale(1); opacity: 0.6; }
  50% { transform: scale(1.1); opacity: 0.8; }
}

/* Enhanced Slide container styling */
.slide-container {
  background: rgba(10, 10, 10, 0.98);
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 0 40px rgba(255, 107, 107, 0.8);
  border: 3px solid var(--quasar-yellow);
}

.slide-navigation {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  background: linear-gradient(135deg, var(--stellar-blue) 0%, var(--event-horizon) 100%);
  border-bottom: 2px solid var(--quasar-yellow);
}

.slide-btn {
  background: var(--quasar-yellow);
  color: var(--black-hole-dark);
  border: none;
  padding: 12px 18px;
  font-size: 1.3rem;
  cursor: pointer;
  border-radius: 8px;
  transition: all 0.4s ease;
  box-shadow: 0 0 15px var(--quasar-yellow);
  font-weight: bold;
}

.slide-btn:hover {
  background: var(--accretion-orange);
  color: var(--stellar-white);
  transform: translateY(-3px) scale(1.05);
  box-shadow: 0 8px 25px rgba(255, 107, 107, 0.4);
}

.slide-indicator {
  color: var(--stellar-white);
  font-weight: bold;
  font-size: 1.2rem;
}

.slide-content {
  padding: 40px;
  min-height: 250px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--black-hole-dark) 0%, var(--event-horizon) 100%);
}

.slide {
  text-align: center;
  color: var(--stellar-white);
  max-width: 800px;
}

.slide h3 {
  color: var(--quasar-yellow);
  margin-bottom: 25px;
  font-size: 2rem;
  text-shadow: 0 0 20px var(--quasar-yellow);
}

.slide p {
  margin-bottom: 25px;
  line-height: 1.7;
  font-size: 1.1rem;
}

.slide-image {
  font-size: 3.5rem;
  animation: pulse 6s ease-in-out infinite;
}

/* Enhanced Planet grid styling */
.planet-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 25px;
  margin: 25px 0;
}

.planet {
  background: rgba(10, 10, 10, 0.98);
  border-radius: 15px;
  padding: 25px;
  text-align: center;
  cursor: pointer;
  transition: all 0.5s ease;
  border: 3px solid var(--quasar-yellow);
  background-image: var(--black-hole-pattern-5);
  background-size: 120px 120px;
  position: relative;
  overflow: hidden;
}

.planet::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(45deg, transparent, rgba(255, 107, 107, 0.1), transparent);
  animation: shimmer 3s infinite;
}

@keyframes shimmer {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(100%); }
}

.planet:hover {
  transform: translateY(-8px) scale(1.08);
  box-shadow: 0 15px 50px rgba(255, 107, 107, 0.5);
  border-color: var(--accretion-orange);
  background-size: 140px 140px;
}

.planet-icon {
  font-size: 3.5rem;
  margin-bottom: 15px;
  animation: rotate 6s linear infinite;
}

.planet h3 {
  color: var(--quasar-yellow);
  margin-bottom: 15px;
  font-size: 1.3rem;
  text-shadow: 0 0 15px var(--quasar-yellow);
}

.planet-info {
  color: var(--stellar-white);
  font-size: 1rem;
  opacity: 0.85;
}

.planet-details {
  background: rgba(10, 10, 10, 0.98);
  border-radius: 15px;
  padding: 25px;
  margin-top: 25px;
  border: 3px solid var(--quasar-yellow);
  background-image: var(--black-hole-pattern-3);
  background-size: 160px 160px;
  transition: opacity 0.6s ease;
  position: relative;
  overflow: hidden;
}

.planet-details::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, var(--quasar-yellow), var(--accretion-orange));
}

.planet-details h3 {
  color: var(--quasar-yellow);
  margin-bottom: 15px;
  font-size: 1.5rem;
  text-shadow: 0 0 15px var(--quasar-yellow);
}

.planet-details p {
  color: var(--stellar-white);
  line-height: 1.7;
  font-size: 1.1rem;
}

/* Enhanced Booking buttons styling */
.booking-buttons {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.booking-buttons button {
  background: linear-gradient(135deg, var(--quasar-yellow) 0%, var(--accretion-orange) 100%);
  color: var(--black-hole-dark);
  border: none;
  padding: 18px 36px;
  font-size: 1.2rem;
  cursor: pointer;
  border-radius: 12px;
  margin: 12px 0;
  transition: all 0.5s ease;
  text-transform: uppercase;
  letter-spacing: 1.5px;
  font-weight: bold;
  box-shadow: 0 6px 25px rgba(255, 107, 107, 0.3);
  position: relative;
  overflow: hidden;
}

.booking-buttons button::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s ease;
}

.booking-buttons button:hover::before {
  left: 100%;
}

.booking-buttons button:hover {
  transform: translateY(-4px) scale(1.05);
  box-shadow: 0 10px 35px rgba(255, 107, 107, 0.4);
}

.booking-buttons button:active {
  transform: translateY(0) scale(1);
  box-shadow: 0 4px 20px rgba(255, 107, 107, 0.3);
}

.booking-buttons button:focus {
  outline: 3px solid var(--quasar-yellow);
  outline-offset: 2px;
  box-shadow: 0 0 0 6px rgba(255, 107, 107, 0.3);
}

/* Enhanced Contact buttons styling */
.contact-buttons {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.contact-buttons button {
  background: linear-gradient(135deg, var(--accretion-orange) 0%, var(--neutron-purple) 100%);
  color: var(--stellar-white);
  border: none;
  padding: 18px 36px;
  font-size: 1.2rem;
  cursor: pointer;
  border-radius: 12px;
  margin: 12px 0;
  transition: all 0.5s ease;
  text-transform: uppercase;
  letter-spacing: 1.5px;
  font-weight: bold;
  box-shadow: 0 6px 25px rgba(155, 89, 155, 0.3);
  position: relative;
  overflow: hidden;
}

.contact-buttons button::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s ease;
}

.contact-buttons button:hover::before {
  left: 100%;
}

.contact-buttons button:hover {
  transform: translateY(-4px) scale(1.05);
  box-shadow: 0 10px 35px rgba(155, 89, 155, 0.4);
}

.contact-buttons button:active {
  transform: translateY(0) scale(1);
  box-shadow: 0 4px 20px rgba(155, 89, 155, 0.3);
}

.contact-buttons button:focus {
  outline: 3px solid var(--quasar-yellow);
  outline-offset: 2px;
  box-shadow: 0 0 0 6px rgba(155, 89, 155, 0.3);
}

/* Enhanced Responsive design for slides and planets */
@media (max-width: 768px) {
  .slide-container {
    margin: 0 -15px;
    border-radius: 0;
  }

  .slide-navigation {
    padding: 15px;
  }

  .slide-content {
    padding: 25px;
  }

  .planet-grid {
    grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
    gap: 20px;
  }

  .planet {
    padding: 20px;
  }

  .planet-icon {
    font-size: 2.8rem;
  }
}

@media (max-width: 480px) {
  .slide-container {
    margin: 0 -10px;
    border-radius: 0;
  }

  .slide-navigation {
    padding: 12px;
  }

  .slide-btn {
    padding: 10px 16px;
    font-size: 1.1rem;
  }

  .slide-content {
    padding: 20px;
  }

  .slide h3 {
    font-size: 1.8rem;
  }

  .planet-grid {
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: 15px;
  }

  .planet {
    padding: 18px;
  }

  .planet-icon {
    font-size: 2.2rem;
  }

  .planet h3 {
    font-size: 1.1rem;
  }

  .planet-info {
    font-size: 0.9rem;
  }
}

/* Enhanced Animation delays for slide navigation */
.prev-slide {
  animation-delay: 0s;
}

.next-slide {
  animation-delay: 0.3s;
}

/* Enhanced Animation delays for planet grid */
.planet:nth-child(1) {
  animation-delay: 0s;
}

.planet:nth-child(2) {
  animation-delay: 0.2s;
}

.planet:nth-child(3) {
  animation-delay: 0.4s;
}

.planet:nth-child(4) {
  animation-delay: 0.6s;
}

.planet:nth-child(5) {
  animation-delay: 0.8s;
}

.planet:nth-child(6) {
  animation-delay: 1s;
}

.planet:nth-child(7) {
  animation-delay: 1.2s;
}

.planet:nth-child(8) {
  animation-delay: 1.4s;
}

/* Enhanced Enhanced hover effects for slides */
.slide-container:hover .slide-btn {
  opacity: 0.9;
}

/* Enhanced Enhanced hover effects for planets */
.planet-grid:hover .planet {
  opacity: 0.85;
}

.planet-grid:hover .planet:hover {
  opacity: 1;
}

/* Enhanced Focus states for accessibility */
.slide-btn:focus,
.planet:focus {
  outline: 3px solid var(--quasar-yellow);
  outline-offset: 3px;
  box-shadow: 0 0 0 8px rgba(255, 107, 107, 0.4);
