  animation: subtitleFade 4s ease-in-out infinite;
}

/* Enhanced Navigation styling */
.mobile-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(30, 58, 138, 0.98);
  backdrop-filter: blur(15px);
  padding: 15px 20px;
  z-index: 1000;
  background-image: var(--black-hole-pattern-3);
  background-size: 250px 250px;
  border-bottom: 2px solid var(--quasar-yellow);
  box-shadow: 0 5px 20px rgba(255, 107, 107, 0.3);
}

.nav-toggle {
  display: block;
  cursor: pointer;
  float: right;
  padding: 12px;
  background: none;
  border: none;
  outline: none;
  z-index: 1001;
  border-radius: 8px;
  transition: all 0.3s ease;
}

.nav-toggle:hover {
  background: rgba(255, 255, 255, 0.1);
}

.bar {
  display: block;
  width: 28px;
  height: 4px;
  margin: 6px auto;
  background: var(--stellar-white);
  transition: 0.4s;
  border-radius: 2px;
}

.nav-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
