  transition: max-height 0.4s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.6s ease-in;
}

.nav-item {
  margin: 12px 0;
}

.nav-link {
  color: var(--stellar-white);
  text-decoration: none;
  font-weight: bold;
  padding: 14px 24px;
