  text-shadow: 0 0 20px var(--quasar-yellow);
  margin-bottom: 25px;
}

/* Enhanced Timeline styling */
.timeline {
  position: relative;
  padding-left: 25px;
  margin: 30px 0;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 3px;
  background: linear-gradient(180deg, var(--quasar-yellow), var(--accretion-orange));
}

.timeline li {
  position: relative;
