  .slide-btn {
    padding: 10px 16px;
    font-size: 1.1rem;
  }

  .slide-content {
    padding: 20px;
  }

  .slide h3 {
    font-size: 1.8rem;
  }

  .planet-grid {
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: 15px;
  }

  .planet {
    padding: 18px;
  }

  .planet-icon {
    font-size: 2.2rem;
  }

  .planet h3 {
    font-size: 1.1rem;
  }

  .planet-info {
    font-size: 0.9rem;
  }
}

/* Enhanced Animation delays for slide navigation */
.prev-slide {
  animation-delay: 0s;
}

.next-slide {
  animation-delay: 0.3s;
}

/* Enhanced Animation delays for planet grid */
.planet:nth-child(1) {
  animation-delay: 0s;
}

.planet:nth-child(2) {
  animation-delay: 0.2s;
}

.planet:nth-child(3) {
  animation-delay: 0.4s;
}

.planet:nth-child(4) {
  animation-delay: 0.6s;
}

.planet:nth-child(5) {
  animation-delay: 0.8s;
}

.planet:nth-child(6) {
  animation-delay: 1s;
}

.planet:nth-child(7) {
  animation-delay: 1.2s;
}

.planet:nth-child(8) {
  animation-delay: 1.4s;
}

/* Enhanced Enhanced hover effects for slides */
.slide-container:hover .slide-btn {
  opacity: 0.9;
}

/* Enhanced Enhanced hover effects for planets */
.planet-grid:hover .planet {
  opacity: 0.85;
}

.planet-grid:hover .planet:hover {
  opacity: 1;
}

/* Enhanced Focus states for accessibility */
.slide-btn:focus,
.planet:focus {
  outline: 3px solid var(--quasar-yellow);
  outline-offset: 3px;
  box-shadow: 0 0 0 8px rgba(255, 107, 107, 0.4);
  transform: scale(1.08);
}

/* Enhanced Print styles */
@media print {
  .mobile-nav,
  #quote-btn,
  .black-hole-decorations,
  .black-hole-header,
  .black-hole-footer,
  .slide-container,
  .planet-grid {
    display: none;
  }

  body {
    background: white;
    color: black;
  }

  .card {
    box-shadow: none;
    border: 1px solid #ccc;
  }

  .slide {
    page-break-inside: avoid;
  }

  .planet-details {
    page-break-inside: avoid;
  }
}

/* Enhanced Additional animations for interactive elements */
@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.slide {
  animation: slideIn 0.8s ease-out;
}

.planet {
  animation: slideIn 0.8s ease-out;
}

/* Enhanced Enhanced transitions for interactive elements */
.planet,
.slide-btn,
.booking-buttons button,
.contact-buttons button {
  transition: all 0.5s ease;
}

/* Enhanced Improved touch interactions for mobile devices */
@media (hover: none) and (pointer: coarse) {
  .planet {
    min-height: 120px;
  }

  .slide-btn {
    min-height: 52px;
  }

  .booking-buttons button,
  .contact-buttons button {
    min-height: 52px;
  }
}

/* Enhanced Enhanced animations for black hole-related elements */
@keyframes blackHoleSpin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes blackHolePulse {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.4); opacity: 0.7; }
}

@keyframes blackHoleTwinkle {
  0%, 100% { opacity: 0.3; }
  50% { opacity: 1; }
}

@keyframes blackHoleRotate {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes blackHoleScale {
  0% { transform: scale(1); }
  100% { transform: scale(1.15); }
}

/* Enhanced Enhanced parallax effect for black hole elements */
.parallax__layer--base {
  transform: translateZ(0) scale(1);
}

.parallax__layer--back {
  transform: translateZ(-3px) scale(4);
}

.parallax__layer--deep {
  transform: translateZ(-6px) scale(7);
}

