            Current Content:
            :root {
  --bg: #0b1220;
  --surface: #111a2b;
  --accent: #d4af37;
  --text: #e8edf5;
  --muted: #a7b1c2;
  --line: rgba(212, 175, 55, 0.3);
  --egyptian-gold: #d4af37;
  --egyptian-turquoise: #00ced1;
  --egyptian-slate: #1a1a2e;
  --egyptian-sand: #f4e5c2;
  --egyptian-nile: #0a6c64;
  --egyptian-sphinx: #8b6914;
}

* {
  box-sizing: border-box;
}

html,
body {
  margin: 0;
  min-height: 100%;
}

body {
  font-family: "Georgia", "Times New Roman", serif;
  color: var(--text);
  background:
    radial-gradient(90rem 40rem at 10% -5%, #1d2a44 10%, transparent 60%),
    radial-gradient(90rem 50rem at 100% 0%, #152033 0%, transparent 55%),
    var(--bg);
  line-height: 1.6;
}

.hero {
  text-align: center;
  padding: 4.5rem 1.25rem 2.25rem;
  background: linear-gradient(135deg, var(--egyptian-slate), var(--surface));
  border-radius: 1rem;
  margin: 0 1rem;
}

.eyebrow {
  margin: 0;
  color: var(--egyptian-gold);
  text-transform: uppercase;
  letter-spacing: 0.16em;
  font-size: 0.75rem;
}

h1 {
  margin: 0.5rem 0 0.75rem;
  font-size: clamp(2rem, 7vw, 4rem);
  color: var(--egyptian-gold);
  text-shadow: 2px 2px 4px rgba(212, 175, 55, 0.3);
}

.subtitle {
  margin: 0 auto;
  max-width: 44rem;
  color: var(--egyptian-turquoise);
}

button {
  margin-top: 1.2rem;
  border: 2px solid var(--egyptian-gold);
  color: var(--egyptian-gold);
  background: transparent;
  border-radius: 999px;
  padding: 0.7rem 1.1rem;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

button:hover {
  background: rgba(212, 175, 55, 0.3);
  transform: translateY(-2px);
}

.container {
  width: min(56rem, 92vw);
  margin: 0 auto 2rem;
  display: grid;
  gap: 1rem;
}

.card {
  background: rgba(17, 26, 43, 0.86);
  border: 1px solid var(--egyptian-gold);
  border-radius: 0.9rem;
  padding: 1rem 1.1rem;
  position: relative;
  overflow: hidden;
}

.card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, var(--egyptian-gold), var(--egyptian-turquoise));
}

.card::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, var(--egyptian-turquoise), var(--egyptian-gold));
}

h2 {
  margin: 0 0 0.55rem;
  color: var(--egyptian-gold);
  font-size: 1.5rem;
}

blockquote {
  margin: 0;
  font-size: 1.1rem;
  color: var(--egyptian-turquoise);
  font-style: italic;
}

.timeline ul {
  margin: 0;
  padding-left: 1.2rem;
}

.timeline li + li {
  margin-top: 0.45rem;
}

footer {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.85rem;
  flex-wrap: wrap;
  color: var(--muted);
  padding: 1.2rem;
  border-top: 1px solid rgba(255, 255, 255, 0.06);
}

footer p {
  margin: 0;
}

footer a {
  color: var(--egyptian-gold);
  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
}

/* Mobile Responsive Styles */
@media (max-width: 768px) {
  .hero {
    padding: 3rem 1rem 1.5rem;
  }

  h1 {
    font-size: clamp(1.5rem, 5vw, 3rem);
  }

  .subtitle {
    font-size: 0.9rem;
  }

  .container {
    width: 95%;
    margin: 0 auto 1.5rem;
  }

  .card {
    padding: 0.8rem 1rem;
  }

  blockquote {
    font-size: 1rem;
  }

  footer {
    padding: 1rem;
    font-size: 0.9rem;
  }
}

@media (max-width: 480px) {
  .hero {
    padding: 2.5rem 0.5rem 1rem;
  }

  h1 {
    font-size: clamp(1.2rem, 4vw, 2.5rem);
  }

  .container {
    width: 100%;
    margin: 0 auto 1rem;
  }

  .card {
    padding: 0.6rem 0.8rem;
  }

  blockquote {
    font-size: 0.9rem;
  }

  footer {
    padding: 0.8rem;
    font-size: 0.8rem;
  }
}

/* Additional Mobile Responsive Styles for better UX */
@media (max-width: 320px) {
  .hero {
    padding: 2rem 0.3rem 0.8rem;
  }

  h1 {
    font-size: clamp(1.1rem, 3vw, 2rem);
  }

  .container {
    width: 98%;
    margin: 0 auto 0.8rem;
  }

  .card {
    padding: 0.5rem 0.6rem;
  }

  blockquote {
    font-size: 0.85rem;
  }

