/* Christmas-themed enhancements */
.card {
  background: linear-gradient(135deg, rgba(17, 26, 43, 0.86), rgba(23, 32, 51, 0.86));
  border: 2px solid var(--christmas-gold);
  box-shadow: 0 0 20px rgba(212, 175, 55, 0.2);
}

.card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, var(--christmas-gold), var(--christmas-green));
}

.card::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, var(--christmas-green), var(--christmas-gold));
}

button {
  margin-top: 1.2rem;
  border: 2px solid var(--christmas-gold);
  color: var(--christmas-gold);
  background: transparent;
  border-radius: 999px;
  padding: 0.7rem 1.1rem;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

