  overflow: hidden;
}

.card::before {
  content: 
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, var(--christmas-red), var(--christmas-green));
}

.card::after {
  content: 
  position: absolute;
