            Current Content:
            :root {
  --bg: #0b1220;
  --surface: #111a2b;
  --accent: #d4af37;
  --text: #e8edf5;
  --muted: #a7b1c2;
  --line: rgba(212, 175, 55, 0.3);
  --egyptian-gold: #d4af37;
  --egyptian-turquoise: #00ced1;
  --egyptian-slate: #1a1a2e;
  --egyptian-sand: #f4e5c2;
  --egyptian-nile: #0a6c64;
  --egyptian-sphinx: #8b6914;
  --christmas-red: #ff0000;
  --christmas-green: #00ff00;
  --christmas-gold: #d4af37;
  --christmas-white: #ffffff;
  --christmas-dark-green: #006400;
}

* {
  box-sizing: border-box;
}

html,
body {
  margin: 0;
  min-height: 100%;
}

body {
  font-family: "Georgia", "Times New Roman", serif;
  color: var(--text);
  background:
    radial-gradient(90rem 40rem at 10% -5%, #1d2a44 10%, transparent 60%),
    radial-gradient(90rem 50rem at 100% 0%, #152033 0%, transparent 55%),
    var(--bg);
  line-height: 1.6;
}

.hero {
  text-align: center;
  padding: 4.5rem 1.25rem 2.25rem;
  background: linear-gradient(135deg, var(--egyptian-slate), var(--surface));
  border-radius: 1rem;
  margin: 0 1rem;
}

.eyebrow {
  margin: 0;
  color: var(--christmas-red);
  text-transform: uppercase;
  letter-spacing: 0.16em;
  font-size: 0.75rem;
  text-shadow: 1px 1px 2px rgba(255, 0, 0, 0.5), 0 0 10px rgba(255, 0, 0, 0.2);
}

h1 {
  margin: 0.5rem 0 0.75rem;
  font-size: clamp(2rem, 7vw, 4rem);
  color: var(--christmas-red);
  text-shadow: 2px 2px 4px rgba(255, 0, 0, 0.3), 0 0 15px rgba(255, 0, 0, 0.1);
}

.subtitle {
  margin: 0 auto;
  max-width: 44rem;
  color: var(--christmas-green);
  font-style: italic;
  text-shadow: 1px 1px 2px rgba(0, 255, 0, 0.3), 0 0 10px rgba(0, 255, 0, 0.2);
}

button {
  margin-top: 1.2rem;
  border: 2px solid var(--christmas-red);
  color: var(--christmas-red);
  background: transparent;
  border-radius: 999px;
  padding: 0.7rem 1.1rem;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

button:hover {
  background: rgba(255, 0, 0, 0.3);
  transform: translateY(-2px) rotate(1deg);
  box-shadow: 0 5px 15px rgba(255, 0, 0, 0.4);
  animation: christmasGlow 0.5s ease-in-out;
}

.container {
  width: min(56rem, 92vw);
  margin: 0 auto 2rem;
  display: grid;
  gap: 1rem;
}

.card {
  background: linear-gradient(135deg, rgba(17, 26, 43, 0.86), rgba(23, 32, 51, 0.86));
  border: 2px solid var(--christmas-red);
  border-radius: 0.9rem;
  padding: 1rem 1.1rem;
  position: relative;
  overflow: hidden;
}

.card::before {
  content: 
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, var(--christmas-red), var(--christmas-green));
}

.card::after {
  content: 
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, var(--christmas-green), var(--christmas-red));
}

h2 {
  margin: 0 0 0.55rem;
  color: var(--christmas-red);
  font-size: 1.5rem;
}

blockquote {
  margin: 0;
  font-size: 1.1rem;
  color: var(--christmas-green);
  font-style: italic;
  text-shadow: 1px 1px 2px rgba(0, 255, 0, 0.3), 0 0 10px rgba(0, 255, 0, 0.2);
}

.timeline ul {
  margin: 0;
  padding-left: 1.2rem;
}

.timeline li + li {
  margin-top: 0.45rem;
}

footer {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.85rem;
  flex-wrap: wrap;
  color: var(--muted);
  padding: 1.2rem;
  border-top: 2px solid var(--christmas-red);
  background: linear-gradient(135deg, rgba(17, 26, 43, 0.5), rgba(23, 32, 51, 0.5));
}

footer p {
  margin: 0;
}

footer a {
  color: var(--christmas-red);
  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
  color: var(--christmas-green);
  text-shadow: 0 0 5px rgba(0, 255, 0, 0.5);
  animation: christmasGlow 0.5s ease-in-out;
}

/* Christmas-themed decorations */
.christmas-decorations {
  position: absolute;
  top: 10px;
  left: 10px;
  right: 10px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 1;
}

.snowflake {
  font-size: 1.5rem;
  animation: snowflakeFall 3s ease-in-out infinite;
}

@keyframes snowflakeFall {
  0% { transform: translateY(-20px); opacity: 0; }
  50% { opacity: 1; }
  100% { transform: translateY(20px); opacity: 0; }
}

.christmas-tree {
  font-size: 2rem;
  animation: treeGlow 2s ease-in-out infinite alternate;
}

@keyframes treeGlow {
  0% { filter: brightness(1); }
  100% { filter: brightness(1.2); }
}

.star {
  font-size: 1.8rem;
  animation: starPulse 1.5s ease-in-out infinite;
}

@keyframes starPulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.2); }
}

/* Mobile Navigation Enhancements */
.mobile-nav {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 0;
}

.nav-toggle {
  display: none;
  flex-direction: column;
  cursor: pointer;
  padding: 0.5rem;
}

.bar {
  width: 25px;
  height: 3px;
  background-color: var(--christmas-red);
  margin: 3px 0;
  transition: 0.3s;
}

.nav-menu {
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
}

.nav-item {
  margin-left: 1.5rem;
}

.nav-link {
  color: var(--christmas-red);
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s;
}

.nav-link:hover {
  color: var(--christmas-green);
}

/* Mobile Navigation Toggle */
@media (max-width: 768px) {
  .nav-toggle {
    display: flex;
  }

  .nav-menu {
    position: absolute;
    left: -100%;
    top: 100%;
    flex-direction: column;
    background-color: var(--surface);
    width: 100%;
    text-align: center;
    transition: 0.3s;
    box-shadow: 0 10px 27px rgba(0, 0, 0, 0.05);
