/* Enhanced Hover Effects for Buttons and Links */
button {
  margin-top: 1.2rem;
  border: 2px solid var(--christmas-red);
  color: var(--christmas-red);
  background: transparent;
  border-radius: 999px;
  padding: 0.7rem 1.1rem;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

button:hover {
  background: rgba(255, 0, 0, 0.3);
  transform: translateY(-2px) rotate(1deg);
  box-shadow: 0 5px 15px rgba(255, 0, 0, 0.4);
  animation: christmasGlow 0.5s ease-in-out;
}

.nav-link {
  color: var(--christmas-red);
  text-decoration: none;
  font-weight: 500;
  transition: color 0.3s;
}

.nav-link:hover {
  color: var(--christmas-green);
  text-shadow: 0 0 3px rgba(0, 255, 0, 0.5);
  transform: translateY(-1px);
}

footer a {
  color: var(--christmas-red);
  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
  color: var(--christmas-green);
  text-shadow: 0 0 5px rgba(0, 255, 0, 0.5);
  animation: christmasGlow 0.5s ease-in-out;
}

/* Enhanced Hover Effects for Cards */
.card {
  background: linear-gradient(135deg, rgba(17, 26, 43, 0.86), rgba(23, 32, 51, 0.86));
  border: 2px solid var(--christmas-red);
  border-radius: 0.9rem;
  padding: 1rem 1.1rem;
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
}

.card:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 25px rgba(255, 0, 0, 0.2);
  border-color: var(--christmas-green);
}

/* Enhanced Hover Effects for Quote */
blockquote:hover {
  transform: scale(1.02);
  box-shadow: 0 5px 15px rgba(0, 255, 0, 0.3);
}

/* Enhanced Hover Effects for Footer */
footer:hover {
  background: linear-gradient(135deg, rgba(255, 0, 0, 0.1), rgba(0, 255, 0, 0.1));
}

/* Enhanced Hover Effects for Christmas Decorations */
.christmas-decorations:hover .snowflake {
  animation: snowflakeFall 1s ease-in-out infinite;
}

.christmas-decorations:hover .christmas-tree {
  animation: treeGlow 1s ease-in-out infinite alternate;
}

.christmas-decorations:hover .star {
  animation: starPulse 0.75s ease-in-out infinite;
}

/* Enhanced Hover Effects for Christmas Header */
.christmas-header:hover .christmas-greeting {
  text-shadow: 3px 3px 6px rgba(255, 0, 0, 0.5);
}

.christmas-header:hover .christmas-snow .snow {
  animation: snowflakeFall 1s ease-in-out infinite;
}

/* Enhanced Hover Effects for Christmas Footer */
.christmas-footer:hover .christmas-wishes {
  text-shadow: 2px 2px 4px rgba(0, 255, 0, 0.5);
}

.christmas-footer:hover .christmas-bell {
  animation: bellRing 1s ease-in-out infinite;
}

/* Enhanced Hover Effects for Christmas Footer Decorations */
.christmas-footer-decorations:hover .footer-snowflake {
  animation: snowflakeFall 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-candy-cane {
  animation: candyCaneGlow 1s ease-in-out infinite alternate;
}

.christmas-footer-decorations:hover .footer-gift {
  animation: giftBounce 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-reindeer {
  animation: reindeerWiggle 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-wreath {
  animation: wreathSpin 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-ornament {
  animation: ornamentPulse 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-bell {
  animation: bellRing 1s ease-in-out infinite;
}

/* Additional Animations */
@keyframes candyCaneGlow {
  0% { filter: brightness(1); }
  100% { filter: brightness(1.2); }
}

@keyframes giftBounce {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-5px); }
}

@keyframes reindeerWiggle {
  0%, 100% { transform: rotate(0deg); }
  50% { transform: rotate(5deg); }
}

@keyframes wreathSpin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes ornamentPulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.1); }
}

