  transform: scale(1.02);
  box-shadow: 0 5px 15px rgba(0, 255, 0, 0.3);
}

/* Enhanced Hover Effects for Footer */
footer:hover {
  background: linear-gradient(135deg, rgba(255, 0, 0, 0.1), rgba(0, 255, 0, 0.1));
}

/* Enhanced Hover Effects for Christmas Decorations */
.christmas-decorations:hover .snowflake {
  animation: snowflakeFall 1s ease-in-out infinite;
}

.christmas-decorations:hover .christmas-tree {
  animation: treeGlow 1s ease-in-out infinite alternate;
}

.christmas-decorations:hover .star {
  animation: starPulse 0.75s ease-in-out infinite;
}

/* Enhanced Hover Effects for Christmas Header */
.christmas-header:hover .christmas-greeting {
  text-shadow: 3px 3px 6px rgba(255, 0, 0, 0.5);
}

.christmas-header:hover .christmas-snow .snow {
  animation: snowflakeFall 1s ease-in-out infinite;
}

