.christmas-footer:hover .christmas-wishes {
  text-shadow: 2px 2px 4px rgba(0, 255, 0, 0.5);
}

.christmas-footer:hover .christmas-bell {
  animation: bellRing 1s ease-in-out infinite;
}

/* Enhanced Hover Effects for Christmas Footer Decorations */
.christmas-footer-decorations:hover .footer-snowflake {
  animation: snowflakeFall 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-candy-cane {
  animation: candyCaneGlow 1s ease-in-out infinite alternate;
}

.christmas-footer-decorations:hover .footer-gift {
  animation: giftBounce 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-reindeer {
  animation: reindeerWiggle 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-wreath {
  animation: wreathSpin 1s ease-in-out infinite;
}

.christmas-footer-decorations:hover .footer-ornament {
  animation: ornamentPulse 1s ease-in-out infinite;
