:root {
  --sand-light: #f4e8c1;
  --sand-dark: #d2b55b;
  --desert-gold: #d4af37;
  --ancient-brown: #8b6914;
  --nile-blue: #1e3a8a;
  --papyrus-green: #7ea04d;
  --scarab-green: #308014;
  --hieroglyph-black: #0a0a0a;
  --hieroglyph-gold: #d4af37;
}

/* General styling */
* {
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: 'Georgia', serif;
  line-height: 1.6;
  color: var(--hieroglyph-black);
  background: linear-gradient(135deg, var(--sand-light) 0%, var(--sand-dark) 100%);
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
}

/* Header styling */
.hero {
  text-align: center;
  padding: 60px 20px;
  background: linear-gradient(135deg, var(--desert-gold) 0%, var(--sand-dark) 100%);
  color: var(--hieroglyph-black);
  position: relative;
  overflow: hidden;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><path d="M10,10 L90,10 L50,90 Z" fill="%23d4af37" opacity="0.1"/></svg>');
  background-size: 100px 100px;
}

.hero h1 {
  font-size: 3.5rem;
  margin: 0;
  font-family: 'Times New Roman', serif;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
}

.subtitle {
  font-size: 1.2rem;
  margin: 10px 0;
  font-style: italic;
  color: var(--hieroglyph-black);
}

/* Navigation styling */
.mobile-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(212, 175, 55, 0.95);
  backdrop-filter: blur(10px);
  padding: 10px 20px;
  z-index: 1000;
}

.nav-toggle {
  display: block;
  cursor: pointer;
  float: right;
  padding: 10px;
  background: none;
  border: none;
  outline: none;
  z-index: 1001;
}

.bar {
  display: block;
  width: 25px;
  height: 3px;
  margin: 5px auto;
  background: var(--hieroglyph-black);
  transition: 0.3s;
}

.nav-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.5s ease-in;
}

.nav-item {
  margin: 10px 0;
}

.nav-link {
  color: var(--hieroglyph-black);
  text-decoration: none;
  font-weight: bold;
  padding: 10px 20px;
  display: inline-block;
  transition: background-color 0.3s;
}

.nav-link:hover {
  background-color: rgba(0, 0, 0, 0.1);
}

/* Card styling */
.card {
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  margin: 20px 0;
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  border: 2px solid var(--desert-gold);
}

.card h2 {
  color: var(--hieroglyph-black);
  margin-top: 0;
  font-size: 2rem;
  text-align: center;
}

/* Timeline styling */
.timeline {
  position: relative;
  padding-left: 20px;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 2px;
  background: var(--desert-gold);
}

.timeline li {
  position: relative;
  margin-bottom: 20px;
}

.timeline li::before {
  content: '';
  position: absolute;
  left: -24px;
  top: 5px;
  width: 10px;
  height: 10px;
  background: var(--desert-gold);
  border-radius: 50%;
}

/* Footer styling */
footer {
  text-align: center;
  padding: 30px 20px;
  background: var(--hieroglyph-black);
  color: var(--sand-light);
  margin-top: 40px;
}

footer a {
  color: var(--desert-gold);
  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
}

/* Egyptian-themed decorations */
.egyptian-decorations {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
}

.pyramid {
  position: absolute;
  font-size: 2rem;
  animation: float 3s ease-in-out infinite;
}

.pyramid:nth-child(1) {
  top: 10%;
  left: 10%;
  animation-delay: 0s;
}

.pyramid:nth-child(2) {
  top: 20%;
  right: 10%;
  animation-delay: 1s;
}

.pyramid:nth-child(3) {
  bottom: 10%;
  left: 20%;
  animation-delay: 2s;
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

.hieroglyph {
  position: absolute;
  font-size: 1.5rem;
  animation: spin 4s linear infinite;
}

.hieroglyph:nth-child(4) {
  top: 30%;
  left: 50%;
  animation-delay: 0s;
}

.hieroglyph:nth-child(5) {
  bottom: 30%;
  right: 50%;
  animation-delay: 2s;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.anubis {
  position: absolute;
  font-size: 2rem;
  animation: bounce 2s ease-in-out infinite;
}

.anubis:nth-child(6) {
  top: 50%;
  left: 50%;
  animation-delay: 1s;
}

@keyframes bounce {
  0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
  40% { transform: translateY(-30px); }
  60% { transform: translateY(-15px); }
}

/* Responsive design */
@media (max-width: 768px) {
  .hero h1 {
    font-size: 2.5rem;
  }

  .card {
    padding: 20px;
  }

  .timeline::before {
    left: 10px;
  }

  .timeline li::before {
    left: -19px;
  }
}

@media (max-width: 480px) {
  .hero h1 {
    font-size: 2rem;
  }

  .card {
    padding: 15px;
  }

  .container {
    padding: 10px;
  }
}

/* Egyptian-themed header additions */
.egyptian-header {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  padding: 20px;
  text-align: center;
  color: var(--hieroglyph-gold);
}

.egyptian-greeting {
  font-size: 1.2rem;
  font-style: italic;
  margin-bottom: 10px;
}

.egyptian-hieroglyphs {
  display: flex;
  justify-content: center;
  gap: 10px;
}

/* Egyptian-themed footer additions */
.egyptian-footer {
  margin-top: 20px;
  padding-top: 20px;
  border-top: 1px solid var(--desert-gold);
}

.egyptian-wishes {
  margin-bottom: 10px;
  font-style: italic;
  color: var(--desert-gold);
}

.egyptian-footer-decorations {
  display: flex;
  justify-content: center;
  gap: 10px;
  font-size: 1.5rem;
}

.footer-pyramid {
  animation: float 3s ease-in-out infinite;
}

.footer-hieroglyph {
  animation: spin 4s linear infinite;
}

.footer-anubis {
  animation: bounce 2s ease-in-out infinite;
}

.footer-scarab {
  animation: spin 2s linear infinite;
}

.footer-nile {
  animation: float 4s ease-in-out infinite;
}

.footer-sphinx {
  animation: bounce 3s ease-in-out infinite;
}

.footer-eye {
  animation: spin 5s linear infinite;
}

/* Quote button styling */
#quote-btn {
  background: var(--desert-gold);
  color: var(--hieroglyph-black);
  border: none;
  padding: 15px 30px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin: 20px 0;
  transition: background-color 0.3s;
}

#quote-btn:hover {
  background: var(--ancient-brown);
  color: var(--sand-light);
}

#quote-btn:active {
  transform: scale(0.98);
}

/* Quote styling */
#quote-text {
  font-style: italic;
  font-size: 1.2rem;
  color: var(--hieroglyph-black);
  margin: 20px 0;
  padding: 20px;
  background: rgba(212, 175, 55, 0.1);
  border-left: 4px solid var(--desert-gold);
}

/* Eyebrow styling */
.eyebrow {
  font-size: 1.1rem;
  font-style: italic;
  color: var(--hieroglyph-gold);
  margin-bottom: 10px;
}

/* Smooth scrolling */
html {
  scroll-behavior: smooth;
}

/* Focus styles for accessibility */
.nav-link:focus,
#quote-btn:focus {
  outline: 2px solid var(--desert-gold);
  outline-offset: 2px;
}

/* Print styles */
@media print {
  .mobile-nav,
  #quote-btn,
  .egyptian-decorations,
  .egyptian-header,
  .egyptian-footer {
    display: none;
  }

  body {
    background: white;
    color: black;
  }

  .card {
    box-shadow: none;
    border: 1px solid #ccc;
  }
