:root {
  --sand-light: #f4e8c1;
  --sand-dark: #d2b55b;
  --desert-gold: #d4af37;
  --ancient-brown: #8b6914;
  --nile-blue: #1e3a8a;
  --papyrus-green: #7ea04d;
  --scarab-green: #308014;
  --hieroglyph-black: #0a0a0a;
