  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.5s ease-in;
}

.nav-item {
  margin: 10px 0;
}

.nav-link {
  color: var(--hieroglyph-black);
  text-decoration: none;
  font-weight: bold;
  padding: 10px 20px;
  display: inline-block;
  transition: background-color 0.3s;
}

.nav-link:hover {
  background-color: rgba(0, 0, 0, 0.1);
}

/* Card styling */
.card {
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  margin: 20px 0;
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  border: 2px solid var(--desert-gold);
}

.card h2 {
  color: var(--hieroglyph-black);
  margin-top: 0;
  font-size: 2rem;
  text-align: center;
}

/* Timeline styling */
.timeline {
  position: relative;
  padding-left: 20px;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 2px;
  background: var(--desert-gold);
}

.timeline li {
  position: relative;
  margin-bottom: 20px;
}

.timeline li::before {
  content: '';
  position: absolute;
  left: -24px;
  top: 5px;
  width: 10px;
  height: 10px;
  background: var(--desert-gold);
  border-radius: 50%;
}

/* Footer styling */
footer {
  text-align: center;
  padding: 30px 20px;
