  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
}

/* Egyptian-themed decorations */
.egyptian-decorations {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
}

.pyramid {
  position: absolute;
  font-size: 2rem;
  animation: float 3s ease-in-out infinite;
}

.pyramid:nth-child(1) {
  top: 10%;
  left: 10%;
  animation-delay: 0s;
}

.pyramid:nth-child(2) {
  top: 20%;
  right: 10%;
  animation-delay: 1s;
}

.pyramid:nth-child(3) {
  bottom: 10%;
  left: 20%;
  animation-delay: 2s;
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

.hieroglyph {
  position: absolute;
  font-size: 1.5rem;
  animation: spin 4s linear infinite;
}

.hieroglyph:nth-child(4) {
  top: 30%;
  left: 50%;
  animation-delay: 0s;
}

.hieroglyph:nth-child(5) {
  bottom: 30%;
  right: 50%;
  animation-delay: 2s;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.anubis {
  position: absolute;
  font-size: 2rem;
  animation: bounce 2s ease-in-out infinite;
}

.anubis:nth-child(6) {
  top: 50%;
  left: 50%;
  animation-delay: 1s;
}

@keyframes bounce {
  0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
  40% { transform: translateY(-30px); }
  60% { transform: translateY(-15px); }
}

/* Responsive design */
@media (max-width: 768px) {
