            Current Content:
            /* Beach-themed color palette */
:root {
  --sand-light: #f4e8c1;
  --sand-dark: #d2b55b;
  --ocean-blue: #1e3a8a;
  --sunny-yellow: #ffd700;
  --palm-green: #7ea04d;
  --coral-red: #ff6b6b;
  --beach-sky: #87ceeb;
  --sea-foam: #2e8b57;
  --wave-white: #f0ffff;
}

/* Beach-inspired patterns and textures */
:root {
  --beach-pattern-1: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M10,10 L90,10 L50,90 Z" fill="%23ffd700" opacity="0.1"/><circle cx="20" cy="20" r="5" fill="%23ffd700" opacity="0.1"/><circle cx="80" cy="80" r="5" fill="%23ffd700" opacity="0.1"/></svg>');
  --beach-pattern-2: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M10,50 L50,10 L90,50 L50,90 Z" fill="%231e3a8a" opacity="0.1"/><path d="M50,50 L70,30 L50,10 L30,30 Z" fill="%231e3a8a" opacity="0.1"/></svg>');
  --beach-pattern-3: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M20,20 L80,20 L80,80 L20,80 Z" fill="%23ffd700" opacity="0.05"/><path d="M30,30 L70,30 L70,70 L30,70 Z" fill="%23ffd700" opacity="0.05"/></svg>');
  --beach-pattern-4: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M10,10 L50,50 L10,90 L50,50 Z" fill="%231e3a8a" opacity="0.1"/><path d="M90,10 L50,50 L90,90 L50,50 Z" fill="%231e3a8a" opacity="0.1"/></svg>');
}

/* General styling */
* {
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: 'Georgia', serif;
  line-height: 1.6;
  color: var(--ocean-blue);
  background: linear-gradient(135deg, var(--sand-light) 0%, var(--sand-dark) 100%), var(--beach-pattern-1);
  background-size: cover;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 10px;
}

/* Header styling */
.hero {
  text-align: center;
  padding: 60px 20px;
  background: linear-gradient(135deg, var(--sunny-yellow) 0%, var(--sand-dark) 100%), var(--beach-pattern-2);
  color: var(--ocean-blue);
  position: relative;
  overflow: hidden;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><path d="M10,10 L90,10 L50,90 Z" fill="%23ffd700" opacity="0.1"/></svg>');
  background-size: 100px 100px;
}

.hero h1 {
  font-size: 3.5rem;
  margin: 0;
  font-family: 'Times New Roman', serif;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
}

.subtitle {
  font-size: 1.2rem;
  margin: 10px 0;
  font-style: italic;
  color: var(--ocean-blue);
}

/* Navigation styling */
.mobile-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(30, 58, 138, 0.95);
  backdrop-filter: blur(10px);
  padding: 10px 20px;
  z-index: 1000;
  background-image: var(--beach-pattern-3);
  background-size: 200px 200px;
}

.nav-toggle {
  display: block;
  cursor: pointer;
  float: right;
  padding: 10px;
  background: none;
  border: none;
  outline: none;
  z-index: 1001;
}

.bar {
  display: block;
  width: 25px;
  height: 3px;
  margin: 5px auto;
  background: var(--wave-white);
  transition: 0.3s;
}

.nav-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.5s ease-in;
}

.nav-item {
  margin: 10px 0;
}

.nav-link {
  color: var(--wave-white);
  text-decoration: none;
  font-weight: bold;
  padding: 10px 20px;
  display: inline-block;
  transition: background-color 0.3s;
}

.nav-link:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

/* Card styling */
.card {
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  margin: 20px 0;
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  border: 2px solid var(--sunny-yellow);
  background-image: var(--beach-pattern-4);
  background-size: 150px 150px;
}

.card h2 {
  color: var(--ocean-blue);
  margin-top: 0;
  font-size: 2rem;
  text-align: center;
}

/* Timeline styling */
.timeline {
  position: relative;
  padding-left: 20px;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 2px;
  background: var(--sunny-yellow);
}

.timeline li {
  position: relative;
  margin-bottom: 20px;
}

.timeline li::before {
  content: '';
  position: absolute;
  left: -24px;
  top: 5px;
  width: 10px;
  height: 10px;
  background: var(--sunny-yellow);
  border-radius: 50%;
}

/* Footer styling */
footer {
  text-align: center;
  padding: 30px 20px;
  background: var(--ocean-blue);
  color: var(--wave-white);
  margin-top: 40px;
}

footer a {
  color: var(--sunny-yellow);
  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
}

/* Beach-themed decorations */
.beach-decorations {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
}

.sand-castle {
  position: absolute;
  font-size: 2rem;
  animation: float 3s ease-in-out infinite;
}

.sand-castle:nth-child(1) {
  top: 10%;
  left: 10%;
  animation-delay: 0s;
}

.sand-castle:nth-child(2) {
  top: 20%;
  right: 10%;
  animation-delay: 1s;
}

.sand-castle:nth-child(3) {
  bottom: 10%;
  left: 20%;
  animation-delay: 2s;
}

