  --beach-pattern-4: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M10,10 L50,50 L10,90 L50,50 Z" fill="%231e3a8a" opacity="0.1"/><path d="M90,10 L50,50 L90,90 L50,50 Z" fill="%231e3a8a" opacity="0.1"/></svg>');
