/* General styling */
* {
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: 'Georgia', serif;
  line-height: 1.6;
  color: var(--ocean-blue);
  background: linear-gradient(135deg, var(--sand-light) 0%, var(--sand-dark) 100%), var(--beach-pattern-1);
  background-size: cover;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 10px;
}

/* Header styling */
.hero {
  text-align: center;
  padding: 60px 20px;
  background: linear-gradient(135deg, var(--sunny-yellow) 0%, var(--sand-dark) 100%), var(--beach-pattern-2);
  color: var(--ocean-blue);
  position: relative;
  overflow: hidden;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><path d="M10,10 L90,10 L50,90 Z" fill="%23ffd700" opacity="0.1"/></svg>');
  background-size: 100px 100px;
}

.hero h1 {
  font-size: 3.5rem;
  margin: 0;
  font-family: 'Times New Roman', serif;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.1);
}

.subtitle {
  font-size: 1.2rem;
  margin: 10px 0;
  font-style: italic;
  color: var(--ocean-blue);
}

/* Navigation styling */
.mobile-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(30, 58, 138, 0.95);
  backdrop-filter: blur(10px);
  padding: 10px 20px;
  z-index: 1000;
  background-image: var(--beach-pattern-3);
  background-size: 200px 200px;
}

.nav-toggle {
  display: block;
  cursor: pointer;
  float: right;
  padding: 10px;
  background: none;
  border: none;
  outline: none;
  z-index: 1001;
}

.bar {
  display: block;
  width: 25px;
