.nav-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.5s ease-in;
}

.nav-item {
  margin: 10px 0;
}

.nav-link {
  color: var(--wave-white);
  text-decoration: none;
  font-weight: bold;
  padding: 10px 20px;
