}

@keyframes pulse {
  0%, 100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.1);
    opacity: 0.8;
  }
}

/* Summer Button Variants */
.summer-button {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange));
  border: 2px solid var(--summer-blue);
  box-shadow: 0 4px 15px rgba(255, 215, 0, 0.3);
}

.summer-button:hover {
  background: linear-gradient(135deg, var(--summer-orange), var(--summer-yellow));
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(135, 206, 235, 0.3);
}

/* Summer Card Variants */
.summer-card {
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.95), rgba(152, 251, 152, 0.05));
  border: 2px solid var(--summer-blue);
  box-shadow: 0 8px 32px rgba(135, 206, 235, 0.1);
