/* CSS Variables for Summer Theme */
:root {
  --summer-yellow: #FFD700;
  --summer-orange: #FFA500;
  --summer-blue: #87CEEB;
  --summer-green: #98FB98;
  --sun-color: #FFD700;
  --wave-color: #4682B4;
}

/* Summer Background Pattern */
.summer-bg {
  background-image: 
    radial-gradient(circle at 20% 50%, rgba(255, 215, 0, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 80%, rgba(135, 206, 235, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 40% 20%, rgba(152, 251, 152, 0.1) 0%, transparent 50%);
}

/* Sun Rays Animation */
.sun {
  position: fixed;
  top: 10px;
  right: 10px;
  z-index: 1;
  user-select: none;
  pointer-events: none;
}

.sun::before {
  content: "\2600";
  color: var(--sun-color);
  font-size: 3rem;
  text-shadow: 0 0 20px rgba(255, 215, 0, 0.8);
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0%, 100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.1);
    opacity: 0.8;
  }
}

/* Summer Button Variants */
.summer-button {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange));
  border: 2px solid var(--summer-blue);
  box-shadow: 0 4px 15px rgba(255, 215, 0, 0.3);
}

.summer-button:hover {
  background: linear-gradient(135deg, var(--summer-orange), var(--summer-yellow));
