/* General styling */
* {
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: 'Georgia', serif;
  line-height: 1.6;
  color: var(--stellar-white);
  background: linear-gradient(135deg, var(--black-hole-dark) 0%, var(--event-horizon) 100%), var(--black-hole-pattern-1);
  background-size: cover;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: rgba(10, 10, 10, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 10px;
  border: 2px solid var(--quasar-yellow);
}

/* Header styling */
.hero {
  text-align: center;
  padding: 60px 20px;
  background: linear-gradient(135deg, var(--stellar-blue) 0%, var(--event-horizon) 100%), var(--black-hole-pattern-2);
  color: var(--stellar-white);
  position: relative;
  overflow: hidden;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="50" cy="50" r="40" fill="%23ff6b6b" opacity="0.1"/></svg>');
  background-size: 100px 100px;
}

.hero h1 {
  font-size: 3.5rem;
  margin: 0;
  font-family: 'Times New Roman', serif;
  text-shadow: 0 0 20px var(--quasar-yellow), 0 0 40px var(--quasar-yellow);
}

.subtitle {
  font-size: 1.2rem;
  margin: 10px 0;
  font-style: italic;
  color: var(--stellar-white);
  text-shadow: 0 0 10px var(--neutron-purple);
}

/* Navigation styling */
.mobile-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(30, 58, 138, 0.95);
  backdrop-filter: blur(10px);
  padding: 10px 20px;
  z-index: 1000;
  background-image: var(--black-hole-pattern-3);
  background-size: 200px 200px;
}

.nav-toggle {
  display: block;
  cursor: pointer;
  float: right;
  padding: 10px;
  background: none;
  border: none;
  outline: none;
  z-index: 1001;
}

.bar {
  display: block;
  width: 25px;
  height: 3px;
  margin: 5px auto;
  background: var(--stellar-white);
  transition: 0.3s;
}

.nav-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.5s ease-in;
}

.nav-item {
  margin: 10px 0;
}

.nav-link {
  color: var(--stellar-white);
  text-decoration: none;
  font-weight: bold;
  padding: 10px 20px;
  display: inline-block;
  transition: background-color 0.3s;
}

.nav-link:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

/* Card styling */
.card {
  background: rgba(10, 10, 10, 0.95);
  padding: 30px;
  margin: 20px 0;
  border-radius: 10px;
  box-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
  border: 2px solid var(--quasar-yellow);
  background-image: var(--black-hole-pattern-4);
  background-size: 150px 150px;
}

.card h2 {
  color: var(--quasar-yellow);
  margin-top: 0;
  font-size: 2rem;
  text-align: center;
  text-shadow: 0 0 10px var(--quasar-yellow);
}

/* Timeline styling */
.timeline {
  position: relative;
  padding-left: 20px;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 2px;
  background: var(--quasar-yellow);
}

.timeline li {
  position: relative;
  margin-bottom: 20px;
}

.timeline li::before {
  content: '';
  position: absolute;
  left: -24px;
  top: 5px;
  width: 10px;
  height: 10px;
  background: var(--quasar-yellow);
  border-radius: 50%;
}

/* Footer styling */
footer {
  text-align: center;
  padding: 30px 20px;
  background: var(--event-horizon);
  color: var(--stellar-white);
  margin-top: 40px;
  border-top: 1px solid var(--quasar-yellow);
}

footer a {
  color: var(--quasar-yellow);
  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
}

/* Black Hole-themed decorations */
.black-hole-decorations {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
}

.event-horizon {
  position: absolute;
  font-size: 2rem;
  animation: rotate 10s linear infinite;
}

.event-horizon:nth-child(1) {
  top: 10%;
  left: 10%;
  animation-delay: 0s;
}

.event-horizon:nth-child(2) {
  top: 20%;
  right: 10%;
  animation-delay: 2s;
}

.event-horizon:nth-child(3) {
  bottom: 10%;
  left: 20%;
  animation-delay: 4s;
}

.accretion-disk {
  position: absolute;
  font-size: 1.5rem;
  animation: spin 6s linear infinite;
}

.accretion-disk:nth-child(4) {
  top: 30%;
  left: 50%;
  animation-delay: 0s;
}

.accretion-disk:nth-child(5) {
  bottom: 30%;
  right: 50%;
  animation-delay: 3s;
}

.stellar-remnant {
  position: absolute;
  font-size: 2rem;
  animation: pulse 4s ease-in-out infinite;
}

.stellar-remnant:nth-child(6) {
  top: 50%;
  left: 50%;
  animation-delay: 1s;
}

@keyframes rotate {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes pulse {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.2); opacity: 0.8; }
}

