    font-size: 4rem;
  }

  .container {
    padding: 40px;
  }

  .card {
    padding: 35px;
  }

  #quote-btn {
    padding: 18px 32px;
    font-size: 1.1rem;
  }

  #quote-text {
    font-size: 1.3rem;
    padding: 25px;
  }
}

/* Landscape mobile responsive design */
@media (max-width: 767px) and (orientation: landscape) {
  .hero h1 {
    font-size: 2rem;
  }

  .subtitle {
    font-size: 1rem;
  }

  .nav-link {
    padding: 6px 12px;
    font-size: 0.85rem;
  }

  .card {
    padding: 10px;
  }

  .card h2 {
    font-size: 1.4rem;
  }
}

/* Portrait tablet responsive design */
@media (min-width: 481px) and (max-width: 768px) and (orientation: portrait) {
  .hero h1 {
    font-size: 2.3rem;
  }

  .card {
    padding: 20px;
  }

  .card h2 {
    font-size: 1.7rem;
  }
}

/* Enhanced touch interactions for mobile devices */
@media (hover: none) and (pointer: coarse) {
  .nav-link {
    min-height: 44px;
    display: flex;
    align-items: center;
  }

  #quote-btn {
    min-height: 44px;
    min-width: 120px;
  }

  .back-to-top {
    min-height: 44px;
  }

  .card {
    margin: 15px 0;
  }
}

