  if (event.key === 'ArrowLeft' && currentSlideIndex > 0) {
    event.preventDefault();
    prevSlide.click();
  }
  if (event.key === 'ArrowRight' && currentSlideIndex < slides.length - 1) {
    event.preventDefault();
    nextSlide.click();
  }
});

