  --black-hole-pattern-1: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><circle cx="50" cy="50" r="40" fill="%230a0a0a" opacity="0.1"/><circle cx="50" cy="50" r="30" fill="%23ff6b6b" opacity="0.05"/><circle cx="50" cy="50" r="20" fill="%231e3a8a" opacity="0.05"/></svg>');
  --black-hole-pattern-2: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M10,50 L50,10 L90,50 L50,90 Z" fill="%23ff6b6b" opacity="0.1"/><path d="M50,50 L70,30 L50,10 L30,30 Z" fill="%231e3a8a" opacity="0.1"/></svg>');
  --black-hole-pattern-3: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M20,20 L80,20 L80,80 L20,80 Z" fill="%239b59b6" opacity="0.05"/><path d="M30,30 L70,30 L70,70 L30,70 Z" fill="%238e44ad" opacity="0.05"/></svg>');
  --black-hole-pattern-4: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M10,10 L50,50 L10,90 L50,50 Z" fill="%23ffd700" opacity="0.1"/><path d="M90,10 L50,50 L90,90 L50,50 Z" fill="%23ffd700" opacity="0.1"/></svg>');
}

/* General styling */
* {
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: 'Georgia', serif;
  line-height: 1.6;
  color: var(--stellar-white);
  background: linear-gradient(135deg, var(--black-hole-dark) 0%, var(--event-horizon) 100%), var(--black-hole-pattern-1);
  background-size: cover;
  background-attachment: fixed;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: rgba(10, 10, 10, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 10px;
  border: 2px solid var(--quasar-yellow);
}

/* Header styling */
.hero {
  text-align: center;
  padding: 60px 20px;
  background: linear-gradient(135deg, var(--stellar-blue) 0%, var(--event-horizon) 100%), var(--black-hole-pattern-2);
  color: var(--stellar-white);
  position: relative;
  overflow: hidden;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="50" cy="50" r="40" fill="%23ff6b6b" opacity="0.1"/></svg>');
  background-size: 100px 100px;
}

.hero h1 {
  font-size: 3.5rem;
  margin: 0;
  font-family: 'Times New Roman', serif;
  text-shadow: 0 0 20px var(--quasar-yellow), 0 0 40px var(--quasar-yellow);
}

.subtitle {
  font-size: 1.2rem;
  margin: 10px 0;
  font-style: italic;
  color: var(--stellar-white);
  text-shadow: 0 0 10px var(--neutron-purple);
}

/* Navigation styling */
.mobile-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(30, 58, 138, 0.95);
  backdrop-filter: blur(10px);
  padding: 10px 20px;
  z-index: 1000;
  background-image: var(--black-hole-pattern-3);
  background-size: 200px 200px;
}

.nav-toggle {
  display: block;
  cursor: pointer;
  float: right;
  padding: 10px;
  background: none;
  border: none;
  outline: none;
  z-index: 1001;
}

.bar {
  display: block;
  width: 25px;
  height: 3px;
  margin: 5px auto;
  background: var(--stellar-white);
  transition: 0.3s;
}

.nav-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.5s ease-in;
}

.nav-item {
  margin: 10px 0;
}

.nav-link {
  color: var(--stellar-white);
  text-decoration: none;
  font-weight: bold;
  padding: 10px 20px;
  display: inline-block;
  transition: background-color 0.3s;
}

.nav-link:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

/* Card styling */
.card {
  background: rgba(10, 10, 10, 0.95);
  padding: 30px;
  margin: 20px 0;
  border-radius: 10px;
  box-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
  border: 2px solid var(--quasar-yellow);
  background-image: var(--black-hole-pattern-4);
  background-size: 150px 150px;
}

.card h2 {
  color: var(--quasar-yellow);
  margin-top: 0;
  font-size: 2rem;
  text-align: center;
  text-shadow: 0 0 10px var(--quasar-yellow);
}

/* Timeline styling */
.timeline {
  position: relative;
  padding-left: 20px;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 2px;
  background: var(--quasar-yellow);
}

.timeline li {
  position: relative;
  margin-bottom: 20px;
}

.timeline li::before {
  content: '';
  position: absolute;
  left: -24px;
  top: 5px;
  width: 10px;
  height: 10px;
  background: var(--quasar-yellow);
  border-radius: 50%;
}

/* Footer styling */
footer {
  text-align: center;
  padding: 30px 20px;
  background: var(--event-horizon);
  color: var(--stellar-white);
  margin-top: 40px;
  border-top: 1px solid var(--quasar-yellow);
}

footer a {
  color: var(--quasar-yellow);
  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
}

/* Black Hole-themed decorations */
.black-hole-decorations {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
}

.event-horizon {
  position: absolute;
  font-size: 2rem;
  animation: rotate 10s linear infinite;
}

.event-horizon:nth-child(1) {
  top: 10%;
  left: 10%;
  animation-delay: 0s;
}

.event-horizon:nth-child(2) {
  top: 20%;
  right: 10%;
  animation-delay: 2s;
}

.event-horizon:nth-child(3) {
  bottom: 10%;
  left: 20%;
  animation-delay: 4s;
}

.accretion-disk {
  position: absolute;
  font-size: 1.5rem;
  animation: spin 6s linear infinite;
}

.accretion-disk:nth-child(4) {
  top: 30%;
  left: 50%;
  animation-delay: 0s;
}

.accretion-disk:nth-child(5) {
  bottom: 30%;
  right: 50%;
  animation-delay: 3s;
}

.stellar-remnant {
  position: absolute;
  font-size: 2rem;
  animation: pulse 4s ease-in-out infinite;
}

.stellar-remnant:nth-child(6) {
  top: 50%;
  left: 50%;
  animation-delay: 1s;
}

@keyframes rotate {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes pulse {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.2); opacity: 0.8; }
}

/* Parallax effect for background */
.parallax {
  perspective: 1px;
  height: 100vh;
  overflow-x: hidden;
  overflow-y: auto;
}

.parallax__layer {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

.parallax__layer--base {
  transform: translateZ(0);
}

.parallax__layer--back {
  transform: translateZ(-1px) scale(2);
}

.parallax__layer--deep {
  transform: translateZ(-2px) scale(3);
}

/* Parallax stars */
.parallax__stars {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: transparent;
}

.parallax__star {
  position: absolute;
  background: var(--stellar-white);
  border-radius: 50%;
  animation: twinkle 2s infinite;
}

.parallax__star:nth-child(1) {
  width: 1px;
  height: 1px;
  top: 10%;
  left: 20%;
  animation-delay: 0s;
}

.parallax__star:nth-child(2) {
  width: 2px;
  height: 2px;
  top: 50%;
  left: 80%;
  animation-delay: 0.5s;
}

.parallax__star:nth-child(3) {
  width: 1px;
  height: 1px;
  top: 90%;
  left: 40%;
  animation-delay: 1s;
}

@keyframes twinkle {
  0%, 100% { opacity: 0.3; }
  50% { opacity: 1; }
}

/* Parallax galaxies */
.parallax__galaxy {
  position: absolute;
  background: radial-gradient(circle, var(--stellar-blue) 0%, transparent 70%);
  border-radius: 50%;
  animation: rotate 20s linear infinite;
}

.parallax__galaxy:nth-child(1) {
  width: 200px;
  height: 200px;
  top: 20%;
  left: 30%;
  animation-delay: 0s;
}

.parallax__galaxy:nth-child(2) {
  width: 150px;
  height: 150px;
  top: 70%;
  right: 20%;
  animation-delay: 10s;
}

/* Responsive design */
@media (max-width: 768px) {
  .hero h1 {
    font-size: 2.5rem;
  }

  .card {
    padding: 20px;
  }

  .timeline::before {
    left: 10px;
  }

  .timeline li::before {
    left: -19px;
  }
}

@media (max-width: 480px) {
  .hero h1 {
    font-size: 2rem;
  }

  .subtitle {
    font-size: 1rem;
  }

  .nav-link {
    padding: 8px 15px;
    font-size: 0.9rem;
  }

  .card {
    padding: 15px;
  }

  .card h2 {
    font-size: 1.5rem;
  }

  #quote-btn {
    padding: 12px 20px;
    font-size: 0.9rem;
  }

  #quote-text {
    font-size: 1rem;
    padding: 15px;
  }

  .timeline::before {
    left: 5px;
  }

  .timeline li::before {
    left: -14px;
  }
}

@media (min-width: 481px) and (max-width: 768px) {
  .hero h1 {
    font-size: 2.2rem;
  }

  .card {
    padding: 18px;
  }

  .card h2 {
    font-size: 1.8rem;
  }

  #quote-btn {
    padding: 14px 25px;
    font-size: 0.95rem;
  }

  #quote-text {
    font-size: 1.1rem;
    padding: 18px;
  }
}

@media (min-width: 769px) and (max-width: 1024px) {
  .hero h1 {
    font-size: 3rem;
  }

  .container {
    padding: 30px;
  }

  .card {
    padding: 25px;
  }

  #quote-btn {
    padding: 16px 28px;
    font-size: 1.05rem;
  }
}

@media (min-width: 1025px) {
  .hero h1 {
    font-size: 4rem;
  }

  .container {
    padding: 40px;
  }

  .card {
    padding: 35px;
  }

  #quote-btn {
    padding: 18px 32px;
    font-size: 1.1rem;
  }

  #quote-text {
    font-size: 1.3rem;
    padding: 25px;
  }
}

/* Landscape mobile responsive design */
@media (max-width: 767px) and (orientation: landscape) {
  .hero h1 {
    font-size: 2rem;
  }

  .subtitle {
    font-size: 1rem;
  }

  .nav-link {
    padding: 6px 12px;
    font-size: 0.85rem;
  }

  .card {
    padding: 10px;
  }

  .card h2 {
    font-size: 1.4rem;
  }
}

/* Portrait tablet responsive design */
@media (min-width: 481px) and (max-width: 768px) and (orientation: portrait) {
  .hero h1 {
    font-size: 2.3rem;
  }

  .card {
    padding: 20px;
  }

  .card h2 {
    font-size: 1.7rem;
  }
}

/* Enhanced touch interactions for mobile devices */
@media (hover: none) and (pointer: coarse) {
  .nav-link {
    min-height: 44px;
    display: flex;
    align-items: center;
  }

  #quote-btn {
    min-height: 44px;
    min-width: 120px;
  }

  .back-to-top {
    min-height: 44px;
  }

  .card {
    margin: 15px 0;
  }
}

/* Improved button focus states for accessibility */
.nav-link:focus,
#quote-btn:focus,
.back-to-top:focus {
  outline: 3px solid var(--quasar-yellow);
  outline-offset: 2px;
  box-shadow: 0 0 0 2px var(--event-horizon);
}

/* Black Hole-themed header additions */
