}

/* Quote button styling */
#quote-btn {
  background: var(--quasar-yellow);
  color: var(--black-hole-dark);
  border: none;
  padding: 15px 30px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin: 20px 0;
  transition: background-color 0.3s;
  box-shadow: 0 0 10px var(--quasar-yellow);
}

#quote-btn:hover {
  background: var(--accretion-orange);
  color: var(--stellar-white);
  box-shadow: 0 0 20px var(--accretion-orange);
}

#quote-btn:active {
  transform: scale(0.98);
}

/* Quote styling */
#quote-text {
  font-style: italic;
  font-size: 1.2rem;
  color: var(--stellar-white);
  margin: 20px 0;
  padding: 20px;
  background: rgba(255, 215, 0, 0.1);
  border-left: 4px solid var(--quasar-yellow);
  box-shadow: 0 0 10px var(--quasar-yellow);
}

/* Eyebrow styling */
.eyebrow {
  font-size: 1.1rem;
  font-style: italic;
  color: var(--quasar-yellow);
  margin-bottom: 10px;
}

/* Smooth scrolling */
html {
  scroll-behavior: smooth;
}

/* Focus styles for accessibility */
.nav-link:focus,
#quote-btn:focus {
  outline: 2px solid var(--quasar-yellow);
  outline-offset: 2px;
}

/* Print styles */
@media print {
  .mobile-nav,
  #quote-btn,
  .black-hole-decorations,
  .black-hole-header,
  .black-hole-footer {
    display: none;
  }

  body {
    background: white;
    color: black;
  }

  .card {
    box-shadow: none;
    border: 1px solid #ccc;
  }
}

/* Button styling */
button {
  background: var(--quasar-yellow);
  color: var(--black-hole-dark);
  border: none;
  padding: 12px 24px;
  font-size: 1rem;
  cursor: pointer;
  border-radius: 5px;
  margin: 10px 5px;
  transition: all 0.3s ease;
  text-transform: uppercase;
  letter-spacing: 1px;
  font-weight: bold;
  box-shadow: 0 0 10px var(--quasar-yellow);
}

button:hover {
  background: var(--accretion-orange);
  color: var(--stellar-white);
  transform: translateY(-2px);
  box-shadow: 0 5px 15px rgba(255, 107, 107, 0.3);
}

button:active {
  transform: translateY(0);
  box-shadow: 0 3px 10px rgba(255, 107, 107, 0.2);
}

button:focus {
  outline: 2px solid var(--quasar-yellow);
  outline-offset: 2px;
}

