/* Parallax effect for background */
.parallax {
  perspective: 1px;
  height: 100vh;
  overflow-x: hidden;
  overflow-y: auto;
}

.parallax__layer {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

.parallax__layer--base {
  transform: translateZ(0);
}

.parallax__layer--back {
  transform: translateZ(-1px) scale(2);
}

.parallax__layer--deep {
  transform: translateZ(-2px) scale(3);
}

/* Parallax stars */
.parallax__stars {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: transparent;
}

.parallax__star {
  position: absolute;
  background: var(--stellar-white);
  border-radius: 50%;
  animation: twinkle 2s infinite;
}

.parallax__star:nth-child(1) {
  width: 1px;
  height: 1px;
  top: 10%;
  left: 20%;
  animation-delay: 0s;
}

.parallax__star:nth-child(2) {
  width: 2px;
  height: 2px;
  top: 50%;
  left: 80%;
  animation-delay: 0.5s;
}

.parallax__star:nth-child(3) {
  width: 1px;
  height: 1px;
  top: 90%;
  left: 40%;
  animation-delay: 1s;
}

@keyframes twinkle {
  0%, 100% { opacity: 0.3; }
  50% { opacity: 1; }
}

/* Parallax galaxies */
.parallax__galaxy {
  position: absolute;
  background: radial-gradient(circle, var(--stellar-blue) 0%, transparent 70%);
