    "new_content": "/* Black Hole-themed color palette */
:root {
  --black-hole-dark: #000000;
  --event-horizon: #0a0a0a;
  --stellar-blue: #1e3a8a;
  --accretion-orange: #ff6b6b;
  --neutron-purple: #9b59b6;
  --cosmic-lavender: #8e44ad;
  --stellar-white: #f0ffff;
  --quasar-yellow: #ffd700;
  --interstellar-green: #2ecc71;
}

/* Black Hole-inspired patterns and textures */
:root {
  --black-hole-pattern-1: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><circle cx="50" cy="50" r="40" fill="%230a0a0a" opacity="0.1"/><circle cx="50" cy="50" r="30" fill="%23ff6b6b" opacity="0.05"/><circle cx="50" cy="50" r="20" fill="%231e3a8a" opacity="0.05"/></svg>');
  --black-hole-pattern-2: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M10,50 L50,10 L90,50 L50,90 Z" fill="%23ff6b6b" opacity="0.1"/><path d="M50,50 L70,30 L50,10 L30,30 Z" fill="%231e3a8a" opacity="0.1"/></svg>');
  --black-hole-pattern-3: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M20,20 L80,20 L80,80 L20,80 Z" fill="%239b59b6" opacity="0.05"/><path d="M30,30 L70,30 L70,70 L30,70 Z" fill="%238e44ad" opacity="0.05"/></svg>');
  --black-hole-pattern-4: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><rect width="100" height="100" fill="none"/><path d="M10,10 L50,50 L10,90 L50,50 Z" fill="%23ffd700" opacity="0.1"/><path d="M90,10 L50,50 L90,90 L50,50 Z" fill="%23ffd700" opacity="0.1"/></svg>');
}

/* General styling */
* {
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: 'Georgia', serif;
  line-height: 1.6;
  color: var(--stellar-white);
  background: linear-gradient(135deg, var(--black-hole-dark) 0%, var(--event-horizon) 100%), var(--black-hole-pattern-1);
  background-size: cover;
  background-attachment: fixed;
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  background: rgba(10, 10, 10, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 10px;
  border: 2px solid var(--quasar-yellow);
}

/* Header styling */
.hero {
  text-align: center;
  padding: 60px 20px;
  background: linear-gradient(135deg, var(--stellar-blue) 0%, var(--event-horizon) 100%), var(--black-hole-pattern-2);
  color: var(--stellar-white);
  position: relative;
  overflow: hidden;
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><circle cx="50" cy="50" r="40" fill="%23ff6b6b" opacity="0.1"/></svg>');
  background-size: 100px 100px;
}

.hero h1 {
  font-size: 3.5rem;
  margin: 0;
  font-family: 'Times New Roman', serif;
  text-shadow: 0 0 20px var(--quasar-yellow), 0 0 40px var(--quasar-yellow);
}

.subtitle {
  font-size: 1.2rem;
  margin: 10px 0;
  font-style: italic;
  color: var(--stellar-white);
  text-shadow: 0 0 10px var(--neutron-purple);
}

/* Navigation styling */
.mobile-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(30, 58, 138, 0.95);
  backdrop-filter: blur(10px);
  padding: 10px 20px;
  z-index: 1000;
  background-image: var(--black-hole-pattern-3);
  background-size: 200px 200px;
}

.nav-toggle {
  display: block;
  cursor: pointer;
  float: right;
  padding: 10px;
  background: none;
  border: none;
  outline: none;
  z-index: 1001;
}

.bar {
  display: block;
  width: 25px;
  height: 3px;
  margin: 5px auto;
  background: var(--stellar-white);
  transition: 0.3s;
}

.nav-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-out;
}

.nav-menu.active {
  max-height: 500px;
  transition: max-height 0.5s ease-in;
}

.nav-item {
  margin: 10px 0;
}

.nav-link {
  color: var(--stellar-white);
  text-decoration: none;
  font-weight: bold;
  padding: 10px 20px;
  display: inline-block;
  transition: background-color 0.3s;
}

.nav-link:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

/* Card styling */
.card {
  background: rgba(10, 10, 10, 0.95);
  padding: 30px;
  margin: 20px 0;
  border-radius: 10px;
  box-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
  border: 2px solid var(--quasar-yellow);
  background-image: var(--black-hole-pattern-4);
  background-size: 150px 150px;
}

.card h2 {
  color: var(--quasar-yellow);
  margin-top: 0;
  font-size: 2rem;
  text-align: center;
  text-shadow: 0 0 10px var(--quasar-yellow);
}

/* Timeline styling */
.timeline {
  position: relative;
  padding-left: 20px;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 2px;
  background: var(--quasar-yellow);
}

.timeline li {
  position: relative;
  margin-bottom: 20px;
}

.timeline li::before {
  content: '';
  position: absolute;
  left: -24px;
  top: 5px;
  width: 10px;
  height: 10px;
  background: var(--quasar-yellow);
  border-radius: 50%;
}

/* Footer styling */
footer {
  text-align: center;
  padding: 30px 20px;
  background: var(--event-horizon);
  color: var(--stellar-white);
  margin-top: 40px;
  border-top: 1px solid var(--quasar-yellow);
}

footer a {
  color: var(--quasar-yellow);
  text-decoration: none;
}

footer a:hover {
  text-decoration: underline;
}

/* Black Hole-themed decorations */
.black-hole-decorations {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
}

.event-horizon {
  position: absolute;
  font-size: 2rem;
  animation: rotate 10s linear infinite;
}

.event-horizon:nth-child(1) {
  top: 10%;
  left: 10%;
  animation-delay: 0s;
}

.event-horizon:nth-child(2) {
  top: 20%;
  right: 10%;
  animation-delay: 2s;
}

.event-horizon:nth-child(3) {
  bottom: 10%;
  left: 20%;
  animation-delay: 4s;
}

.accretion-disk {
  position: absolute;
  font-size: 1.5rem;
  animation: spin 6s linear infinite;
}

.accretion-disk:nth-child(4) {
  top: 30%;
  left: 50%;
  animation-delay: 0s;
}

.accretion-disk:nth-child(5) {
  bottom: 30%;
  right: 50%;
  animation-delay: 3s;
}

.stellar-remnant {
  position: absolute;
  font-size: 2rem;
  animation: pulse 4s ease-in-out infinite;
}

.stellar-remnant:nth-child(6) {
  top: 50%;
  left: 50%;
  animation-delay: 1s;
}

@keyframes rotate {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes pulse {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.2); opacity: 0.8; }
}

/* Parallax effect for background */
.parallax {
  perspective: 1px;
  height: 100vh;
  overflow-x: hidden;
  overflow-y: auto;
}

.parallax__layer {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

.parallax__layer--base {
  transform: translateZ(0);
}

.parallax__layer--back {
  transform: translateZ(-1px) scale(2);
}

.parallax__layer--deep {
  transform: translateZ(-2px) scale(3);
}

/* Parallax stars */
.parallax__stars {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: transparent;
}

.parallax__star {
  position: absolute;
  background: var(--stellar-white);
  border-radius: 50%;
  animation: twinkle 2s infinite;
}

.parallax__star:nth-child(1) {
  width: 1px;
  height: 1px;
  top: 10%;
  left: 20%;
  animation-delay: 0s;
}

.parallax__star:nth-child(2) {
  width: 2px;
  height: 2px;
  top: 50%;
  left: 80%;
  animation-delay: 0.5s;
}

.parallax__star:nth-child(3) {
  width: 1px;
  height: 1px;
  top: 90%;
  left: 40%;
  animation-delay: 1s;
}

@keyframes twinkle {
  0%, 100% { opacity: 0.3; }
  50% { opacity: 1; }
}

/* Parallax galaxies */
.parallax__galaxy {
  position: absolute;
  background: radial-gradient(circle, var(--stellar-blue) 0%, transparent 70%);
  border-radius: 50%;
  animation: rotate 20s linear infinite;
}

.parallax__galaxy:nth-child(1) {
  width: 200px;
  height: 200px;
  top: 20%;
  left: 30%;
  animation-delay: 0s;
}

.parallax__galaxy:nth-child(2) {
  width: 150px;
  height: 150px;
  top: 70%;
  right: 20%;
  animation-delay: 10s;
}

/* Slide container styling */
.slide-container {
  background: rgba(10, 10, 10, 0.95);
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
  border: 2px solid var(--quasar-yellow);
}

.slide-navigation {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px;
  background: linear-gradient(135deg, var(--stellar-blue) 0%, var(--event-horizon) 100%);
}

.slide-btn {
  background: var(--quasar-yellow);
  color: var(--black-hole-dark);
  border: none;
  padding: 10px 15px;
  font-size: 1.2rem;
  cursor: pointer;
  border-radius: 5px;
  transition: all 0.3s ease;
  box-shadow: 0 0 10px var(--quasar-yellow);
}

.slide-btn:hover {
  background: var(--accretion-orange);
  color: var(--stellar-white);
  transform: translateY(-2px);
  box-shadow: 0 5px 15px rgba(255, 107, 107, 0.3);
}

.slide-indicator {
  color: var(--stellar-white);
  font-weight: bold;
}

.slide-content {
  padding: 30px;
  min-height: 200px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--black-hole-dark) 0%, var(--event-horizon) 100%);
}

.slide {
  text-align: center;
  color: var(--stellar-white);
}

.slide h3 {
  color: var(--quasar-yellow);
  margin-bottom: 20px;
  font-size: 1.5rem;
  text-shadow: 0 0 10px var(--quasar-yellow);
}

.slide p {
  margin-bottom: 20px;
  line-height: 1.6;
}

.slide-image {
  font-size: 3rem;
  animation: pulse 4s ease-in-out infinite;
}

/* Planet grid styling */
.planet-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
  margin: 20px 0;
}

.planet {
  background: rgba(10, 10, 10, 0.95);
  border-radius: 10px;
  padding: 20px;
  text-align: center;
  cursor: pointer;
  transition: all 0.3s ease;
  border: 2px solid var(--quasar-yellow);
  background-image: var(--black-hole-pattern-4);
  background-size: 100px 100px;
}

.planet:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 30px rgba(255, 107, 107, 0.3);
  border-color: var(--accretion-orange);
}

.planet-icon {
  font-size: 3rem;
  margin-bottom: 10px;
  animation: rotate 5s linear infinite;
}

.planet h3 {
  color: var(--quasar-yellow);
  margin-bottom: 10px;
  font-size: 1.2rem;
}

.planet-info {
  color: var(--stellar-white);
  font-size: 0.9rem;
  opacity: 0.8;
}

.planet-details {
  background: rgba(10, 10, 10, 0.95);
  border-radius: 10px;
  padding: 20px;
  margin-top: 20px;
  border: 2px solid var(--quasar-yellow);
  background-image: var(--black-hole-pattern-3);
  background-size: 150px 150px;
  transition: opacity 0.5s ease;
}

.planet-details h3 {
  color: var(--quasar-yellow);
  margin-bottom: 10px;
}

.planet-details p {
  color: var(--stellar-white);
  line-height: 1.6;
}

/* Booking buttons styling */
.booking-buttons {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.booking-buttons button {
  background: linear-gradient(135deg, var(--quasar-yellow) 0%, var(--accretion-orange) 100%);
  color: var(--black-hole-dark);
  border: none;
  padding: 15px 30px;
  font-size: 1.1rem;
  cursor: pointer;
  border-radius: 10px;
  margin: 10px 0;
  transition: all 0.3s ease;
  text-transform: uppercase;
  letter-spacing: 1px;
  font-weight: bold;
  box-shadow: 0 4px 15px rgba(255, 107, 107, 0.2);
}

.booking-buttons button:hover {
  transform: translateY(-3px);
  box-shadow: 0 6px 20px rgba(255, 107, 107, 0.3);
}

.booking-buttons button:active {
  transform: translateY(0);
  box-shadow: 0 3px 10px rgba(255, 107, 107, 0.2);
}

.booking-buttons button:focus {
  outline: 2px solid var(--quasar-yellow);
  outline-offset: 2px;
}

/* Contact buttons styling */
.contact-buttons {
  display: flex;
  flex-direction: column;
  gap: 15px;
}

.contact-buttons button {
  background: linear-gradient(135deg, var(--accretion-orange) 0%, var(--neutron-purple) 100%);
  color: var(--stellar-white);
  border: none;
  padding: 15px 30px;
  font-size: 1.1rem;
  cursor: pointer;
  border-radius: 10px;
  margin: 10px 0;
  transition: all 0.3s ease;
  text-transform: uppercase;
  letter-spacing: 1px;
  font-weight: bold;
  box-shadow: 0 4px 15px rgba(155, 89, 155, 0.2);
}

.contact-buttons button:hover {
  transform: translateY(-3px);
  box-shadow: 0 6px 20px rgba(155, 89, 155, 0.3);
}

.contact-buttons button:active {
  transform: translateY(0);
  box-shadow: 0 3px 10px rgba(155, 89, 155, 0.2);
}

.contact-buttons button:focus {
  outline: 2px solid var(--quasar-yellow);
  outline-offset: 2px;
}

/* Responsive design for slides and planets */
@media (max-width: 768px) {
  .slide-container {
    margin: 0 -20px;
    border-radius: 0;
  }

  .slide-navigation {
    padding: 10px;
  }

  .slide-content {
    padding: 20px;
  }

  .planet-grid {
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 15px;
  }

  .planet {
    padding: 15px;
  }

  .planet-icon {
    font-size: 2.5rem;
  }
}

@media (max-width: 480px) {
  .slide-container {
    margin: 0 -15px;
    border-radius: 0;
  }

  .slide-navigation {
    padding: 8px;
  }

  .slide-btn {
    padding: 8px 12px;
    font-size: 1rem;
  }

  .slide-content {
    padding: 15px;
  }

  .slide h3 {
    font-size: 1.2rem;
  }

  .planet-grid {
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: 10px;
  }

  .planet {
    padding: 12px;
  }

  .planet-icon {
    font-size: 2rem;
  }

  .planet h3 {
    font-size: 1rem;
  }

  .planet-info {
    font-size: 0.8rem;
  }
}

/* Animation delays for slide navigation */
.prev-slide {
  animation-delay: 0s;
}

.next-slide {
  animation-delay: 0.2s;
}

/* Animation delays for planet grid */
.planet:nth-child(1) {
  animation-delay: 0s;
}

.planet:nth-child(2) {
  animation-delay: 0.1s;
}

.planet:nth-child(3) {
  animation-delay: 0.2s;
}

.planet:nth-child(4) {
  animation-delay: 0.3s;
}

.planet:nth-child(5) {
  animation-delay: 0.4s;
}

.planet:nth-child(6) {
  animation-delay: 0.5s;
}

.planet:nth-child(7) {
  animation-delay: 0.6s;
}

.planet:nth-child(8) {
  animation-delay: 0.7s;
}

/* Enhanced hover effects for slides */
.slide-container:hover .slide-btn {
  opacity: 0.8;
}

/* Enhanced hover effects for planets */
.planet-grid:hover .planet {
  opacity: 0.8;
}

.planet-grid:hover .planet:hover {
  opacity: 1;
}

/* Focus states for accessibility */
.slide-btn:focus,
.planet:focus {
  outline: 2px solid var(--quasar-yellow);
  outline-offset: 2px;
  box-shadow: 0 0 0 4px rgba(255, 107, 107, 0.3);
}

/* Print styles */
@media print {
  .mobile-nav,
  #quote-btn,
  .black-hole-decorations,
  .black-hole-header,
  .black-hole-footer,
  .slide-container,
  .planet-grid {
    display: none;
  }

  body {
    background: white;
    color: black;
  }

  .card {
    box-shadow: none;
    border: 1px solid #ccc;
  }
}

/* Additional animations for interactive elements */
@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.slide {
  animation: slideIn 0.5s ease-out;
}

.planet {
  animation: slideIn 0.5s ease-out;
}

/* Enhanced transitions for interactive elements */
.planet,
.slide-btn,
.booking-buttons button,
.contact-buttons button {
  transition: all 0.3s ease;
}

/* Improved touch interactions for mobile devices */
@media (hover: none) and (pointer: coarse) {
  .planet {
    min-height: 100px;
  }

  .slide-btn {
    min-height: 44px;
  }

  .booking-buttons button,
  .contact-buttons button {
    min-height: 44px;
  }
}

/* Enhanced animations for black hole-related elements */
@keyframes blackHoleSpin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes blackHolePulse {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.2); opacity: 0.8; }
}

@keyframes blackHoleTwinkle {
  0%, 100% { opacity: 0.3; }
  50% { opacity: 1; }
}

@keyframes blackHoleRotate {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes blackHoleScale {
  0% { transform: scale(1); }
  100% { transform: scale(1.1); }
}

/* Enhanced parallax effect for black hole elements */
.parallax__layer--base {
  transform: translateZ(0) scale(1);
}

.parallax__layer--back {
  transform: translateZ(-1px) scale(2);
}

.parallax__layer--deep {
  transform: translateZ(-2px) scale(3);
}

/* Enhanced hover effects for interactive elements */
.planet:hover {
  transform: translateY(-5px) scale(1.05);
  box-shadow: 0 10px 30px rgba(255, 107, 107, 0.3);
}

.slide-btn:hover {
  background: var(--accretion-orange);
  color: var(--stellar-white);
  transform: translateY(-2px) scale(1.05);
  box-shadow: 0 5px 15px rgba(255, 107, 107, 0.3);
}

.booking-buttons button:hover {
  transform: translateY(-3px) scale(1.05);
  box-shadow: 0 6px 20px rgba(255, 107, 107, 0.3);
}

.contact-buttons button:hover {
  transform: translateY(-3px) scale(1.05);
  box-shadow: 0 6px 20px rgba(155, 89, 155, 0.3);
}

/* Enhanced focus states for accessibility */
.slide-btn:focus,
.planet:focus {
  outline: 2px solid var(--quasar-yellow);
  outline-offset: 2px;
  box-shadow: 0 0 0 4px rgba(255, 107, 107, 0.3);
  transform: scale(1.05);
}

/* Enhanced print styles */
@media print {
  .mobile-nav,
  #quote-btn,
  .black-hole-decorations,
  .black-hole-header,
  .black-hole-footer,
  .slide-container,
  .planet-grid {
    display: none;
  }

  body {
    background: white;
    color: black;
  }

  .card {
    box-shadow: none;
    border: 1px solid #ccc;
  }

  .slide {
    page-break-inside: avoid;
  }
}"
}

