  }

  .planet {
    padding: 12px;
  }

  .planet-icon {
    font-size: 2rem;
  }

  .planet h3 {
    font-size: 1rem;
  }

  .planet-info {
    font-size: 0.8rem;
  }
}

/* Animation delays for slide navigation */
.prev-slide {
  animation-delay: 0s;
}

.next-slide {
  animation-delay: 0.2s;
}

/* Animation delays for planet grid */
.planet:nth-child(1) {
  animation-delay: 0s;
