  background: linear-gradient(135deg, rgba(255, 255, 255, 0.95), rgba(152, 251, 152, 0.05), rgba(255, 105, 180, 0.02));
  border: 2px solid var(--summer-blue);
  box-shadow: 0 8px 32px rgba(135, 206, 235, 0.1), 0 0 30px rgba(255, 105, 180, 0.1);
  backdrop-filter: blur(10px);
  position: relative;
  overflow: hidden;
}

.summer-card::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: 
    repeating-linear-gradient(
      45deg,
      transparent,
      transparent 10px,
      rgba(152, 251, 152, 0.05) 10px,
      rgba(152, 251, 152, 0.05) 20px
    ),
    radial-gradient(circle at 50% 50%, rgba(255, 105, 180, 0.1) 0%, transparent 50%);
  pointer-events: none;
}

.summer-card::after {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 215, 0, 0.3), transparent);
  transition: left 0.8s;
}

.summer-card:hover::after {
  left: 100%;
}

/* Enhanced Summer Badge Variants */
.summer-badge {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-pink));
  color: white;
  border: 1px solid var(--summer-blue);
  box-shadow: 0 2px 8px rgba(255, 215, 0, 0.3), 0 0 10px rgba(255, 105, 180, 0.2);
  position: relative;
  overflow: hidden;
}

.summer-badge::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transform: translateX(-100%);
  transition: transform 0.3s;
}

.summer-badge:hover::before {
  transform: translateX(100%);
}

.summer-badge:hover {
  background: linear-gradient(135deg, var(--summer-orange), var(--summer-yellow), var(--summer-pink));
  transform: translateY(-2px);
  box-shadow: 0 3px 12px rgba(135, 206, 235, 0.3), 0 0 15px rgba(255, 105, 180, 0.3);
}

/* Enhanced Summer Header Styles */
.summer-header {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-blue), var(--summer-pink), var(--summer-purple));
  color: white;
  position: relative;
  overflow: hidden;
}

.summer-header::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: repeating-linear-gradient(
    45deg,
    transparent,
    transparent 20px,
    rgba(152, 251, 152, 0.3) 20px,
    rgba(152, 251, 152, 0.3) 40px
  );
  pointer-events: none;
  animation: shimmer 3s infinite, float 4s ease-in-out infinite;
}

@keyframes shimmer {
  0% {
    opacity: 0.5;
  }
  50% {
    opacity: 1;
  }
  100% {
    opacity: 0.5;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-5px);
  }
}

/* Enhanced Summer Text Effects */
.summer-text {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-blue), var(--summer-pink), var(--summer-purple));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 0 0 15px rgba(255, 215, 0, 0.5), 0 0 10px rgba(255, 105, 180, 0.3);
}

/* Enhanced Summer Border Glow */
.summer-border {
  border: 2px solid var(--summer-blue);
  box-shadow: 0 0 20px rgba(255, 215, 0, 0.3), 0 0 15px rgba(255, 105, 180, 0.2);
}

/* Enhanced Summer Shadow Effects */
.summer-shadow {
  box-shadow: 0 10px 30px rgba(255, 215, 0, 0.2), 0 4px 15px rgba(135, 206, 235, 0.1), 0 0 20px rgba(255, 105, 180, 0.1);
}

/* Enhanced Wave Animation */
.wave {
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100px;
  background: linear-gradient(180deg, transparent, var(--wave-color));
  z-index: 0;
  pointer-events: none;
}

.wave::before,
.wave::after {
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  background: var(--wave-color);
  opacity: 0.5;
}

.wave::before {
  bottom: 0;
  animation: wave 6s cubic-bezier(0.36, 0.45, 0.63, 0.53) infinite, float 5s ease-in-out infinite;
}

.wave::after {
  bottom: 0;
  animation: wave 7s cubic-bezier(0.36, 0.45, 0.63, 0.53) infinite, float 5s ease-in-out infinite;
  animation-delay: -2s;
}

@keyframes wave {
  0% {
    transform: translateX(0) translateZ(0) scaleY(1);
  }
  50% {
    transform: translateX(-25%) translateZ(0) scaleY(0.8);
  }
