}

/* Enhanced Wave Animation */
.wave {
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 80px;
  background: linear-gradient(180deg, transparent, rgba(70, 130, 180, 0.3));
  z-index: 0;
  pointer-events: none;
  border-radius: 100% 100% 0 0;
}

.wave::before,
.wave::after {
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  background: rgba(70, 130, 180, 0.2);
  border-radius: 100% 100% 0 0;
}

.wave::before {
  bottom: 0;
  animation: wave 6s cubic-bezier(0.36, 0.45, 0.63, 0.53) infinite, waveFloat 5s ease-in-out infinite;
}

.wave::after {
  bottom: 0;
  animation: wave 7s cubic-bezier(0.36, 0.45, 0.63, 0.53) infinite, waveFloat 5s ease-in-out infinite;
  animation-delay: -2s;
}

@keyframes wave {
  0% {
