}

/* Enhanced Summer Shadow Effects */
.summer-shadow {
  box-shadow: 0 10px 30px rgba(255, 215, 0, 0.2), 0 4px 15px rgba(135, 206, 235, 0.1), 0 0 20px rgba(255, 105, 180, 0.1);
}

/* Enhanced Wave Animation */
.wave {
  position: fixed;
