/* CSS Variables for Summer Theme */
:root {
  --summer-yellow: #FFD700;
  --summer-orange: #FFA500;
  --summer-blue: #87CEEB;
  --summer-green: #98FB98;
  --sun-color: #FFD700;
  --wave-color: #4682B4;
  --summer-pink: #FF69B4;
  --summer-purple: #9370DB;
}

/* Enhanced Summer Background Pattern */
.summer-bg {
  background-image: 
    radial-gradient(circle at 20% 50%, rgba(255, 215, 0, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 80%, rgba(135, 206, 235, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 40% 20%, rgba(152, 251, 152, 0.1) 0%, transparent 50%),
    radial-gradient(circle at 60% 60%, rgba(255, 105, 180, 0.05) 0%, transparent 50%),
    radial-gradient(circle at 10% 90%, rgba(147, 112, 219, 0.05) 0%, transparent 50%);
}

/* Enhanced Sun Rays Animation */
.sun {
  position: fixed;
  top: 10px;
  right: 10px;
  z-index: 1;
  user-select: none;
  pointer-events: none;
}

.sun::before {
  content: "\2600";
  color: var(--sun-color);
  font-size: 3.5rem;
  text-shadow: 0 0 25px rgba(255, 215, 0, 0.9);
  animation: pulse 2s infinite, float 3s ease-in-out infinite;
}

@keyframes pulse {
  0%, 100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.2);
    opacity: 0.8;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-10px);
  }
}

/* Enhanced Summer Button Variants */
.summer-button {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-pink));
  border: 2px solid var(--summer-blue);
  box-shadow: 0 4px 15px rgba(255, 215, 0, 0.3), 0 0 20px rgba(255, 105, 180, 0.2);
  position: relative;
  overflow: hidden;
}

.summer-button::before {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transition: left 0.5s;
}

.summer-button:hover::before {
  left: 100%;
}

.summer-button:hover {
  background: linear-gradient(135deg, var(--summer-orange), var(--summer-yellow), var(--summer-pink));
  transform: translateY(-3px);
  box-shadow: 0 6px 20px rgba(135, 206, 235, 0.3), 0 0 25px rgba(255, 105, 180, 0.3);
}

/* Enhanced Summer Card Variants */
.summer-card {
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.95), rgba(152, 251, 152, 0.05), rgba(255, 105, 180, 0.02));
  border: 2px solid var(--summer-blue);
  box-shadow: 0 8px 32px rgba(135, 206, 235, 0.1), 0 0 30px rgba(255, 105, 180, 0.1);
  backdrop-filter: blur(10px);
  position: relative;
  overflow: hidden;
}

.summer-card::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: 
    repeating-linear-gradient(
      45deg,
      transparent,
      transparent 10px,
      rgba(152, 251, 152, 0.05) 10px,
      rgba(152, 251, 152, 0.05) 20px
    ),
    radial-gradient(circle at 50% 50%, rgba(255, 105, 180, 0.1) 0%, transparent 50%);
  pointer-events: none;
}

.summer-card::after {
  content: "";
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 215, 0, 0.3), transparent);
  transition: left 0.8s;
}

.summer-card:hover::after {
  left: 100%;
}

/* Enhanced Summer Badge Variants */
.summer-badge {
  background: linear-gradient(135deg, var(--summer-yellow), var(--summer-orange), var(--summer-pink));
  color: white;
  border: 1px solid var(--summer-blue);
  box-shadow: 0 2px 8px rgba(255, 215, 0, 0.3), 0 0 10px rgba(255, 105, 180, 0.2);
  position: relative;
  overflow: hidden;
}

.summer-badge::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.3), transparent);
